import React, { Component } from "react";
import { Button, TextareaAutosize } from "@material-ui/core";
import { Modal } from "react-bootstrap";
import { withCookies } from "react-cookie";

class ShowFollowers extends Component {
  constructor(props) {
    super(props);

    this.state = {
      modalShow: true,
      followers: [],
    };
  }

  onHide = () => {
    this.setState({ modelShow: false });
    this.props.onChangeFollowers(false);
  };

  componentDidMount() {
      console.log("h");
    if (this.props.followers && this.props.followers.length > 0) {
      const tempUsers = [];
      console.log("h");
      for (let index = 0; index < this.props.follower.length; index++) {
        fetch(
          `${process.env.REACT_APP_API_URL}/uapi/userDetail/${this.props.follower[index].user_id}/`,
          {
            method: "GET",
            headers: {
              "Content-Type": "application/json",
            },
          }
        )
          .then((resp) => resp.json())
          .then((resp) => {
              console.log(resp)
            tempUsers.push(resp);
          });
      }
      this.setState({ followers: tempUsers });
    }
  }

  render() {
    return (
      <div>
        <Modal
          show={this.state.modalShow}
          size="md"
          aria-labelledby="contained-modal-title-vcenter"
          centered
          onHide={this.onHide}
        >
          <Modal.Header closeButton>Edit About</Modal.Header>
          <Modal.Body className="profile__modal">
            {this.state.followers.map((follower) => (
              <h1>{follower.username}</h1>
            ))}
          </Modal.Body>
        </Modal>
      </div>
    );
  }
}

export default ShowFollowers;
