import React, { Component } from "react";
import { Avatar, Button, TextField } from "@material-ui/core";
import { Modal } from "react-bootstrap";
import { withCookies } from "react-cookie";

class About extends Component {
  constructor(props) {
    super(props);

    this.state = {
      modelShow: true,
      about: "",
    };
  }

  inputChanged = (event) => {
    console.log(event.target.value);
    this.setState({ about: event.target.value });
  };

  render() {
    return (
      <Modal
        show={this.state.modelShow}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
        onHide={() => this.setState({ modelShow: false })}
      >
        <Modal.Header closeButton>Edit intro</Modal.Header>
        <Modal.Body className="profile__modal">
          <img
            src="https://resi.ze-robot.com/dl/4k/4k-desktop-wallpaper.-1920%C3%971200.jpg"
            alt="background"
          />
          <Avatar src="https://static.hollywoodreporter.com/sites/default/files/2019/03/avatar-publicity_still-h_2019-compressed.jpg"></Avatar>
          <div style={{ display: "flex  " }}>
            <TextField
              className="mt-3 mb-5 mr-2"
              variant="outlined"
              size="small"
              type="text"
              fullWidth
              id="firstName"
              value={this.state.credentials.firstName}
              label="First Name"
              name="firstName"
              onChange={this.inputChanged}
              required
            />
            <TextField
              className="mt-3 mb-5 "
              variant="outlined"
              size="small"
              type="text"
              fullWidth
              id="lastName"
              value={this.state.credentials.lastName}
              label="Last Name"
              name="lastName"
              onChange={this.inputChanged}
              required
            />
          </div>
          {/* Headline */}
          <TextField
            variant="outlined"
            size="small"
            type="text"
            fullWidth
            id="headLine"
            value={this.state.credentials.headLine}
            label="HeadLine"
            name="headLine"
            onChange={this.inputChanged}
            required
            className="mb-3"
          />
          {/* Country */}

          <TextField
            variant="outlined"
            size="small"
            type="text"
            fullWidth
            id="country"
            value={this.state.credentials.country}
            label="Country"
            name="country"
            onChange={this.inputChanged}
            required
            className="mb-3"
          />
          {/* Location */}

          <TextField
            variant="outlined"
            size="small"
            type="text"
            fullWidth
            id="location"
            value={this.state.credentials.location}
            label="Location"
            name="location"
            onChange={this.inputChanged}
            required
            className="mb-3"
          />
          {/* Industry */}

          <TextField
            variant="outlined"
            size="small"
            type="text"
            fullWidth
            id="industry"
            value={this.state.credentials.industry}
            label="Industry"
            name="industry"
            onChange={this.inputChanged}
            required
            className="mb-3"
          />
        </Modal.Body>
        <Modal.Footer>
          {this.props.cookies.get("profile") === "true" ? (
            <Button
              onClick={this.editProfile}
              style={save_button}
              type="submit"
            >
              save
            </Button>
          ) : (
            <Button
              onClick={this.createProfile}
              style={save_button}
              type="submit"
            >
              Save
            </Button>
          )}
        </Modal.Footer>
      </Modal>
    );
  }
}

export default About;
