import React, { Component } from "react";
import { Avatar } from "@material-ui/core";
import "./Comments.css";
import { withCookies } from "react-cookie";
import { Container, Row, Col } from "react-bootstrap";

class Comments extends Component {
  constructor(props) {
    super(props);

    this.state = {
      user: "",
      has_commented: false,
    };
  }

  componentDidMount() {
    this.fetchUser(this.props.comment.user);
    if (
      this.props.post.likes.some(
        (e) => e.user == this.props.cookies.get("auth-token").user.id
      )
    ) {
      this.state.has_commented = true;
    }
  }

  fetchUser = (id) => {
    fetch(`http://127.0.0.1:8000/uapi/users/${id}/`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((resp) => resp.json())
      .then((res) =>
        this.setState({
          user: res,
        })
      )
      .catch((error) => console.log(error));
  };

  render() {
    return (
      <React.Fragment>
        <div className="comment">
          <div className="comment__header">
            {this.state.user.profile_pic ? (
              <Avatar src={this.state.user.profile_pic} alt="Profile" />
            ) : (
              <Avatar src="/images/user.svg" alt="Profile" />
            )}
            <div className="comment__info">
              <div class="img_wrp">
                <span style={{ fontWeight: "bold", fontSize: "18px" }}>
                  {this.state.user.username}
                </span>
                <br />
                <span style={{ fontSize: "16px" }}>
                  {this.props.comment.comment}
                </span>
                <li-icon className="close__image">
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    viewBox="0 0 16 16"
                    data-supported-dps="16x16"
                    fill="currentColor"
                    class="mercado-match"
                    width="16"
                    height="16"
                    focusable="false"
                  >
                    <path d="M3 9.5A1.5 1.5 0 114.5 8 1.5 1.5 0 013 9.5zM11.5 8A1.5 1.5 0 1013 6.5 1.5 1.5 0 0011.5 8zm-5 0A1.5 1.5 0 108 6.5 1.5 1.5 0 006.5 8z"></path>
                  </svg>
                </li-icon>
              </div>
            </div>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default withCookies(Comments);
