import React, { Component } from "react";
import InfoOutlinedIcon from "@material-ui/icons/InfoOutlined";
import FiberManualRecordIcon from "@material-ui/icons/FiberManualRecord";
class ProfileRight extends Component {
  newsArticle = (heading, subtitle) => (
    <div className="profile_right__article">
      <div className="profile_right__articleLeft">
        <FiberManualRecordIcon className="mr-1" />
      </div>

      <div className="profile_right__articleRight">
        <h4>{heading}</h4>
        <p>{subtitle}</p>
      </div>
    </div>
  );

  render() {
    return (
      <div className="profile_right">
        <div className="profile_right__header">
          <h2>Linkedin News</h2>
          <InfoOutlinedIcon />
        </div>
        {this.newsArticle(
          "Elon Musk is now the richest person in the world",
          "Top news - 4296 readers"
        )}
        {this.newsArticle(
          "Study suggests Pfizer vaccine works against virus variant",
          "Top news - 3126 readers"
        )}
        {this.newsArticle(
          "YouTube Suspends Trump’s Channel for at Least Seven Days",
          "Top news - 2926 readers"
        )}
        {this.newsArticle(
          "EHang’s passenger-carrying drone a key step in Urban Air Mobility",
          "Top news - 4231 readers"
        )}

        <img
          src="https://static-exp1.licdn.com/scds/common/u/images/promo/ads/li_evergreen_jobs_ad_300x250_v1.jpg"
          alt="ad"
        />
      </div>
    );
  }
}

export default ProfileRight;
