import React, { Component } from "react";
import DeleteIcon from "@material-ui/icons/Delete";
import { Modal } from "react-bootstrap";
import CameraAltRoundedIcon from "@material-ui/icons/CameraAltRounded";
import { Avatar, Button } from "@material-ui/core";
import "./AddProfilePic.css";
import { withCookies } from "react-cookie";

export class AddProfilePic extends Component {
  constructor(props) {
    super(props);

    this.state = {
      modalShow: true,
      profile_pic: this.props.profile_pic,
      profile_picAsFile: this.props.profile_picAsFile,
    };
  }

  removeImage = () => {
    this.setState({
      profile_pic: "",
      profile_picAsFile: null,
    });
  };

  handleProfilePic = (e) => {
    var form_data = new FormData();

    if (this.state.profile_picAsFile) {
      form_data.set(
        "profile_pic",
        this.state.profile_picAsFile,
        this.state.profile_picAsFile.name
      );
    }
    console.log(this.props.cookies.get("auth-token").token);
    fetch(
      `${process.env.REACT_APP_API_URL}/uapi/users/${
        this.props.cookies.get("auth-token").user.id
      }/update_user/`,
      {
        method: "PUT",
        headers: {
          Authorization: `Token ${this.props.cookies.get("auth-token").token}`,
        },
        body: form_data,
      }
    )
      .then((res) => res.json())
      .then((res) => {
        this.setState({ modalShow: false });
        console.log("response", res);
      })
      .catch((err) => console.log(err));
  };

  render() {
    return (
      <div className="p-auto">
        <Modal
          className="my-modal"
          style={{
            height: "700px",
            color: "white",
          }}
          scrollable={true}
          show={this.state.modalShow}
          size="md"
          aria-labelledby="contained-modal-title-vcenter"
          centered
          onHide={() => this.setState({ modalShow: false })}
        >
          <Modal.Header
            closeButton
            autoFocus
            style={{ borderBottom: "0 none" }}
            // onClick={() => this.setState({ modalShow: false })}
          >
            <Modal.Title
              style={{
                fontSize: "1.2rem",
                color: "white",
              }}
              id="contained-modal-title-vcenter"
            >
              Profile Photo
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Avatar
              className="m-auto"
              style={{ width: "250px", height: "250px", borderRadius: "50%" }}
              src={this.state.profile_pic}
              alt="Profile"
            />
          </Modal.Body>

          <Modal.Footer style={{ borderTopColor: "gray" }}>
            <input
              style={{ display: "none" }}
              type="file"
              id="file"
              onChange={(e) => this.handleProfilePic(e)}
            />
            <label className="mr-auto" htmlFor="file">
              <Button style={profile_button}>Upload Photo</Button>
            </label>
            <CameraAltRoundedIcon
              style={{ color: "white" }}
              onChange={(e) =>
                this.setState({
                  profile_pic: URL.createObjectURL(e.target.files[0]),
                  profile_picAsFile: e.target.files[0],
                })
              }
            />
            <DeleteIcon style={{ color: "white" }} onClick={this.removeImage} />
          </Modal.Footer>
        </Modal>
      </div>
    );
  }
}
const post_button = {
  marginTop: "-10px",
  paddingLeft: "10px",
  paddingRight: "10px",
  marginLeft: "10px",
  fontWeight: "bold",
  borderRadius: "50px",
  // display: "flex",
  background: "#0c66c2",
  color: "white",
  border: "solid 1px #0c66c2",
  width: "110px",
};

export default withCookies(AddProfilePic);
