import React, { Component } from "react";
import "./ProfileRight.css";
import { trackPromise } from "react-promise-tracker";
import FlipMove from "react-flip-move";
import UserContext from "../userContext";
import { Avatar, Button } from "@material-ui/core";

class ProfileRight extends Component {
  static contextType = UserContext;

  constructor(props) {
    super(props);

    this.state = {
      resp_user: [],
      users: [],
    };
  }

  newsArticle = (user) => (
    //   console.log("jjj",user)
    <div className="profile_right__article">
      <div className="profile_right__articleLeft">
        <Avatar
          style={{ width: "80px", height: "80px" }}
          src={user.user.profile_pic}
          alt="Profile"
        />
      </div>

      <div className="profile_right__articleRight">
        <h4>{heading}</h4>
        <p>{subtitle}</p>
      </div>
    </div>
  );

  componentDidMount() {
    const user = this.context;
    // console.log("U", user.user);
    trackPromise(
      fetch(`${process.env.REACT_APP_API_URL}/uapi/userDetail/`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      })
        .then((resp) => resp.json())
        .then((resp) => {
          this.setState({ resp_user: resp }, function () {
            var copy = [];
            for (var i = 0; i < this.state.resp_user.length; i++) {
              if (
                user.user.id != this.state.resp_user[i].id &&
                !user.user.followers.some(
                  (e) => e.user_id === this.state.resp_user[i].id
                ) &&
                !user.user.following.some(
                  (e) => e.following_user_id === this.state.resp_user[i].id
                )
                // &&
                // !user.waitFollowers.some(
                //   (e) => e.user_id === this.state.resp_user[i].id
                // )
              ) {
                copy.push(this.state.resp_user[i]);
              }
            }
            this.setState({ users: copy });
          });
        })
    ).catch((error) => console.log(error));
  }

  render() {
    return (
      <div className="profile_right">
        <div className="profile_right__header">
          <span className="pl-1">People you may know</span>
        </div>
        {this.state.users
          ? this.state.users.map((user) => this.newsArticle({ user }))
          : null}

        {/* {this.newsArticle(
          "Elon Musk is now the richest person in the world",
          "Top news - 4296 readers"
        )} */}
      </div>
    );
  }
}

export default ProfileRight;
