  //       this.setState({ show: true })
  //     )
  //     .catch((error) => console.log(error));
  // };

  modalShow = () => {
    this.setState({ show: true });
  };

  render() {
    const { likes, comments, no_of_like, no_of_comment } = this.props;
    console.log(likes);

    return (
      <div>
        <button onClick={this.modalShow}>{no_of_like}</button>

        {this.state.show ? (
          <Modal
            show={this.state.show}
            size="md"
            aria-labelledby="contained-modal-title-vcenter"
            centered
          >
            <Modal.Header
              closeButton
              onClick={() => {
                this.setState({ show: false });
              }}
            >
              <Modal.Title id="contained-modal-title-vcenter">
                Reactions
              </Modal.Title>
            </Modal.Header>
            <Modal.Body>
              {this.state.users.map((user) => (
                <div>
                  {user.profile_pic ? (
                    <Avatar src={user.profile_pic} alt="Profile" />
                  ) : (
                    <Avatar
                      src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTP9xCw-TO3d5DvvvTaUE2dx6VLYNO52xxG5A&usqp=CAU"
                      alt="Profile"
                    />
                  )}
                  <h4>{user.username}</h4>
                </div>
              ))}
            </Modal.Body>
            <Modal.Footer></Modal.Footer>
          </Modal>
        ) : null}
      </div>
    );
  }
}

export default Count;
