import React, { Component } from "react";
import LikeShow from "./LikeShow";
class Count extends Component {
  constructor(props) {
    super(props);

    this.state = {
      user: "",
      show: false,
      usernames: [],
    };
  }

  // componentDidMount() {
  //   this.LikeInfo(this.state.user);
  // }

  LikeInfo = (id) => {
    fetch(`http://127.0.0.1:8000/uapi/users/${id}/`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Token d194a979f94b27587f743071a64f8d5c7d3dfb01",
      },
    })
      .then((resp) => resp.json())
      .then((res) => [res.username].concat(this.state.usernames)),
      this.setState({ show: true }).catch((error) => console.log(error));
  };
  modalShow = () => {
    this.setState({ show: true });
  };

  render() {
    const { likes, comments, no_of_like, no_of_comment } = this.props;

    return (
      <div>
        {likes.map(
          (like) => (
            this.setState({ user: like.user }, console.log("user", like.user)),
            (
              <div>
                <button onClick={this.modalShow}>{no_of_like}</button>
              </div>
            )
          )
        )}
        {this.state.show ? <LikeShow user={this.state.user} /> : null}
      </div>
    );
  }
}

export default Count;
