import React, { Component } from "react";
import "./Profile.css";
import { Avatar, Typography, Button } from "@material-ui/core";
import { Link } from "react-router-dom";
import { BiPlus, BiEdit } from "react-icons/bi";
import { FiChevronDown, FiChevronUp } from "react-icons/fi";
import EditIntroModal from "./EditInfoModal";
import { withCookies } from "react-cookie";
import About from "./About";
import Education from "./Education";
import License from "./License";
import CameraAltRoundedIcon from "@material-ui/icons/CameraAltRounded";
import AddProfilePic from "./AddProfilePic";
import AddSkills from "./AddSkills";
import EditSkill from "./EditSkill";
import CreatePost from "../Feed/CreatePost";
import CameraAltSharpIcon from "@material-ui/icons/CameraAltSharp";
import AddCoverPic from "./AddCoverPic";

class OtherUserProfile extends Component {
  constructor(props) {
    super(props);

    this.state = {
      user: "",
    };
  }

  componentDidMount() {
    fetch(
      `${process.env.REACT_APP_API_URL}/uapi/users/${this.props.location.state}/`,
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      }
    )
      .then((resp) => resp.json())
      .then((resp) => console.log(resp))
      .catch((error) => console.log(error));
  }

  render() {
    return (
      <div className="profile">
        <div className="profile__top">
          <img src={this.state.user.cover_pic} alt="background" />

          {/* <CameraAltSharpIcon
            style={{ fontSize: "30px" }}
            className="cover__image"
            onClick={() => {
              this.setState({ coverPicModal: true });
            }}
          /> */}
          {this.state.user.profile_pic ? (
            <Avatar
              onClick={() => {
                this.setState({ picShowModal: true });
              }}
              className="img_wrp"
              src={this.state.user.profile_pic}
              alt="Profile"
            />
          ) : (
            <Avatar
              onClick={() => {
                this.setState({ picShowModal: true });
              }}
              className="post__image img_wrp"
              src="/images/user.svg"
              alt="Profile"
            />
          )}

          {/* {this.state.user.picShowModal ? (
            <Link
              component={() => (
                <AddProfilePic
                  profile_pic={this.state.user.profile_pic}
                  profile_picAsFile={this.state.user.profile_picAsFile}
                />
              )}
            ></Link>
          ) : null} */}

          {/* {this.state.user.coverPicModal ? (
            <Link
              component={() => <AddCoverPic cover_pic={this.state.user.cover_pic} />}
            ></Link>
          ) : null} */}

          {/* Create model for user,for add the profile info. */}
          {/* <BiEdit
            className="ml-auto mr-4"
            style={{ fontSize: "30px" }}
            onClick={() => this.onProfileModal(true)}
          /> */}

          {/* For get User information */}
          {/* {this.state.user.profileModalShow ? (
            <Link
              component={() => (
                <EditIntroModal
                  updateProfile={() => this.updateProfile()}
                  onProfileModal={(e) => this.onProfileModal(e)}
                />
              )}
            />
          ) : null} */}

          <div className="profile__stats" style={{ marginTop: "-10px" }}>
            <div className="profile__stat">
              <h4>
                {this.state.user.profileCredentials.firstName
                  ? this.state.user.profileCredentials.firstName
                  : `${
                      this.props.cookies.get("auth-token").user.username
                    }`}{" "}
                {this.state.user.profileCredentials.lastName}
              </h4>
            </div>
            <Typography className="profile__stat">
              {this.state.user.profileCredentials.headLine}
            </Typography>
            <Typography className="profile__stat">
              {this.state.user.profileCredentials.location ? (
                <React.Fragment>
                  {this.state.user.profileCredentials.location}
                  {", "}
                  {this.state.user.profileCredentials.country}
                  <Typography className="profile__stat_connections">
                    {this.state.user.no_of_followers} Connections
                  </Typography>
                </React.Fragment>
              ) : (
                <Typography
                  className="profile__stat_connections"
                  style={{ marginLeft: "-20px" }}
                >
                  {this.state.user.no_of_followers} Connections
                </Typography>
              )}
            </Typography>

            <Link
              className="profile__stat_connections mt-2"
              to={"/contact-info"}
            >
              Contact info
            </Link>

            <div className="profile__stat">
              <Button style={profile_button}>Open To</Button>
              <Button style={profile_button}>Add profile section</Button>
              <Button style={profile_button}>More</Button>
            </div>
          </div>
        </div>

        {/* About */}
        <div className="profile__about mt-3">
          <div className="profile__about_header d-flex justify-content-between">
            <span style={{ fontSize: "25px" }}>About</span>
            <BiEdit
              style={{ fontSize: "30px" }}
              onClick={() => this.onAboutModal(true)}
            />

            {this.state.user.aboutModalShow ? (
              <Link
                component={() => (
                  <About
                    updateAbout={() => this.updateAbout()}
                    onAboutModal={(e) => this.onAboutModal(e)}
                  />
                )}
              />
            ) : null}
          </div>
          <p className="profile__stat_about">
            {this.state.user.AboutCredentials.about}
          </p>
        </div>

        {/* DashBoard*/}
        <div className="profile__dashboard">
          <span
            style={{ fontSize: "25px" }}
            className="profile__dashboard_header d-flex justify-content-between"
          >
            Your Dashboard
          </span>
          <span
            className="profile__dashboard_header d-flex justify-content-between"
            style={{
              marginTop: "-20px",
              fontStyle: "italic",
              color: "#707070	",
            }}
          >
            Private to you
          </span>

          <div className="profile__dashboard_card_1">
            71 who viewd your profile
          </div>
          <div className="profile__dashboard_card_2">1388 post view</div>
        </div>

        {/*  Activity */}

        <div className="profile__activity">
          <div className="profile__activity_header d-flex justify-content-between">
            <span style={{ fontSize: "25px" }}>Activity</span>

            <Button
              size="small"
              style={{ marginTop: "-20px" }}
              onClick={this.onCreatePost}
            >
              Start Post
            </Button>
            {this.state.user.createPost ? (
              <Link component={() => <CreatePost />} />
            ) : null}
          </div>
          <div>
            <Link to={"/followers"}>
              <span
                className="profile__activity_followers"
                style={{ marginTop: "-30px", marginBottom: "10px" }}
              >
                {this.state.user.no_of_followers} followers
              </span>
            </Link>
          </div>
          <div>
            <span
              style={{
                fontSize: "19px",
                paddingRight: "25px",
              }}
            >
              Posts you created, shared, or commented on in the last 90 days are
              displayed here.
            </span>
          </div>
          <div>
            <Link to={"/activity"} style={{ textDecoration: "none" }}>
              <Button
                className="mt-3"
                style={{ width: "100%", marginBottom: "-9px" }}
              >
                See all Activity
              </Button>
            </Link>
          </div>
        </div>

        {/* Education  */}
        <div className="profile__education">
          <div className="profile__education_header d-flex justify-content-between">
            <span style={{ fontSize: "25px" }}>Education</span>
            <BiPlus
              onClick={() => this.onEducationModal(true)}
              style={{ fontSize: "40px" }}
            />

            {this.state.user.educationModalShow ? (
              <Link
                component={() => (
                  <Education
                    onEducationModal={(e) => this.onEducationModal(e)}
                    updateEducation={(e) => this.updateEducation()}
                    onCreateEducation={this.state.user.createEducation}
                    editEducation_id={this.state.user.editEducation_id}
                  />
                )}
              />
            ) : null}
          </div>

          <div>
            {this.getRenderEducation().map((education) => (
              <div>
                <div className="profile__education_header d-flex justify-content-between">
                  <span style={{ fontWeight: "bold", fontSize: "25px" }}>
                    {education.school}
                  </span>
                  <BiEdit
                    style={{ fontSize: "25px" }}
                    onClick={() =>
                      this.setState({
                        educationModalShow: true,
                        createEducation: false,
                        editEducation_id: education.id,
                      })
                    }
                  />
                </div>
                <span
                  style={{ fontSize: "18px", marginTop: "-15px" }}
                  className="d-flex ml-3"
                >
                  {education.degree}
                </span>
                <div>
                  <span
                    style={{ fontSize: "15px", color: "#686868" }}
                    className="d-flex ml-3"
                  >
                    {education.start_year}-{education.end_year}
                  </span>
                </div>
              </div>
            ))}
            {this.state.user.EducationCredentials.length > 3 ? (
              <Button style={{ width: "100%" }} onClick={this.toggleEducation}>
                {this.state.user.showMoreEducation ? "Show Less" : "Show More"}
                {this.state.user.showMoreEducation ? (
                  <FiChevronUp className="ml-2" />
                ) : (
                  <FiChevronDown className="ml-2" />
                )}
              </Button>
            ) : null}
          </div>
        </div>

        {/* License & Certificate */}

        <div className="profile__license">
          <div className="profile__license_header d-flex justify-content-between">
            <span style={{ fontSize: "25px" }}>Licenses & certifications</span>
            <BiPlus
              style={{ fontSize: "40px" }}
              onClick={() => this.onLicenseModal(true)}
            />

            {this.state.user.licenseModalShow ? (
              <Link
                component={() => (
                  <License
                    onLicenseModal={(e) => this.onLicenseModal(e)}
                    updateLicense={(e) => this.updateLicense()}
                    onCreateLicense={this.state.user.createLicense}
                    editLicense_id={this.state.user.editLicense_id}
                  />
                )}
              />
            ) : null}
          </div>

          <div>
            {this.getRenderLicense().map((license) => (
              <div>
                <div className="profile__education_header d-flex justify-content-between">
                  <span style={{ fontWeight: "bold", fontSize: "25px" }}>
                    {license.name}
                  </span>
                  <BiEdit
                    style={{ fontSize: "25px" }}
                    onClick={() =>
                      this.setState({
                        licenseModalShow: true,
                        createLicense: false,
                        editLicense_id: license.id,
                      })
                    }
                  />
                </div>
                <span
                  style={{ fontSize: "18px", marginTop: "-15px" }}
                  className="d-flex ml-3"
                >
                  {license.issuing_org}
                </span>
                <div>
                  {license.issue_date ? (
                    <span
                      style={{ fontSize: "15px", color: "#686868" }}
                      className="d-flex ml-3"
                    >
                      Issued {license.issue_date}{" "}
                      {license.expiration_date ? "-Expiration" : null}{" "}
                      {license.expiration_date}
                    </span>
                  ) : null}
                </div>
              </div>
            ))}
            {this.state.user.LicenseCredentials.length > 3 ? (
              <Button style={{ width: "100%" }} onClick={this.toggleLicense}>
                {this.state.user.showMoreLicense ? "Show Less" : "Show More"}
                {this.state.user.showMoreLicense ? (
                  <FiChevronUp className="ml-2" />
                ) : (
                  <FiChevronDown className="ml-2" />
                )}
              </Button>
            ) : null}
          </div>
        </div>

        {/* Skills & endorsements */}
        <div className="profile__skills">
          <div className="profile__skills_header d-flex bd-highlight">
            <span style={{ fontSize: "25px" }} className="mr-auto">
              Skills & Endorsements
            </span>
            <Button
              className="mr-left"
              size="small"
              style={{ marginTop: "-20px", marginRight: "20px" }}
              onClick={this.onSkillModal}
            >
              Add a new skill
            </Button>
            <BiEdit
              style={{ fontSize: "30px" }}
              onClick={this.onEditSkillModal}
            />
            {this.state.user.skillsModalShow ? (
              <Link
                component={() => (
                  <AddSkills
                    onSkillModal={(e) => this.onSkillModal(e)}
                    updateSkills={(e) => this.updateSkills()}
                  />
                )}
              />
            ) : null}

            {this.state.user.EditSkillsModalShow ? (
              <Link
                component={() => (
                  <EditSkill
                    onEditSkillModal={(e) => this.onEditSkillModal(e)}
                    updateSkills={(e) => this.updateSkills()}
                    SkillCredentials={this.state.user.SkillCredentials}
                  />
                )}
              />
            ) : null}
          </div>

          {this.getRenderSkills().map((skill) => (
            <div>
              <div className="profile__skills_header d-flex justify-content-between">
                <span style={{ fontSize: "25px", fontWeight: "500" }}>
                  {skill.skill}
                </span>
              </div>
            </div>
          ))}
          {this.state.user.SkillCredentials.length > 3 ? (
            <Button style={{ width: "100%" }} onClick={this.toggleSkill}>
              {this.state.user.showMoreSkill ? "Show Less" : "Show More"}
              {this.state.user.showMoreSkill ? (
                <FiChevronUp className="ml-2" />
              ) : (
                <FiChevronDown className="ml-2" />
              )}
            </Button>
          ) : null}
        </div>
      </div>
    );
  }
}

const profile_button = {
    paddingLeft: "20px",
    paddingRight: "20px",
    marginTop: "10px",
    marginLeft: "10px",
    fontWeight: "bold",
    borderRadius: "50px",
    display: "flex",
    color: "#0c66c2",
    border: "solid 1px #0c66c2",
  };

export default OtherUserProfile;
