import React, { Component } from "react";
import "./Login.css";
import { Container, Row } from "react-bootstrap";
import { Typography, Button, Card, TextField, Form } from "@material-ui/core";

class Login extends Component {
  constructor(props) {
    super(props);

    this.state = {
      credentials: {
        // this is for store the user credentials like username and password
        username: "",
        password: "",
      },
    };
  }

  render() {
    return (
      <Container>
        <Row>
          <img
            className="linkedin-img rounded mx-auto d-block"
            src="https://www.logo.wine/a/logo/LinkedIn/LinkedIn-Logo.wine.svg"
            alt="linkedin logo"
          />
        </Row>
        <Row
          style={{
            display: "flex",
            justifyContent: "center",
            marginTop: "30px",
            paddingBottom: "10%",
          }}
        >
          <Card
            className="p-3 shadow p-3 mb-5 bg-white rounded"
            style={{ width: "20rem" }}
          >
            <Typography variant="h4" style={{ fontWeight: "bold" }}>
              Sign in
            </Typography>
            <span style={{ fontSize: "17px" }}>
              Stay updated on your professional world
            </span>
            <form className="m-1">
              <TextField
                variant="outlined"
                type="email"
                fullWidth
                id="username"
                value={this.state.credentials.username}
                label="Email"
                name="username"
                onChange={this.inputChanged}
                required
                className="mt-3 mb-2"
              />

              <TextField
                variant="outlined"
                type="text"
                fullWidth
                id="username"
                value={this.state.credentials.username}
                label="User Name"
                name="username"
                onChange={this.inputChanged}
                required
              />
              <Button
                variant="contained"
                style={{
                  borderRadius: "50px",
                  background: "#0c66c2",
                  color: "#FFFFFF",
                  minWidth: "100%",
                }}
              >
                Sign In
              </Button>
            </form>

            <Typography align="center" className="mt-3">
              Already on LinkedIn?
              <span
                className="ml-1"
                style={{ color: "#0c66c2", fontWeight: "bold" }}
              >
                Sign In
              </span>
            </Typography>
          </Card>
        </Row>
      </Container>
    );
  }
}

export default Login;
