import React, { Component } from "react";
import { Link } from "react-router-dom";
import { MdDone } from "react-icons/md";
import { Avatar, Button } from "@material-ui/core";

class UserCardRight extends Component {
  constructor(props) {
    super(props);

    this.state = {
      pending: false,
    };
  }

  render() {
    return (
      <div className="profile_right__article">
        <div className="profile_right__articleLeft">
          <Avatar
            style={{ width: "60px", height: "60px" }}
            src={this.props.user.profile_pic}
            alt="Profile"
          />
        </div>

        <div className="profile_right__articleRight">
          <Link to={{ pathname: "user_profile", state: this.props.user.id }}>
            <span
              style={{
                fontWeight: "bold",
                color: "black",
                fontSize: "18px",
              }}
            >
              {this.props.user.username.length <= 10
                ? this.props.user.username
                : this.props.user.username.slice(0, 10) + ".."}
            </span>
          </Link>
          {this.state.pending ? (
            <Button
              onClick={() => this.submitFollow(this.props.user.id)}
              className="but mt-3"
              style={pending_button}
            >
              <MdDone style={{ fontSize: "20px", marginRight: "2px" }} />{" "}
              Pending
            </Button>
          ) : (
            <Button
              onClick={() => this.submitFollow(this.props.user.id)}
              className="but mt-3"
              style={connect_button}
            >
              Connect
            </Button>
          )}
        </div>
      </div>
    );
  }
}

const connect_button = {
  fontWeight: "bold",
  borderRadius: "50px",
  fontSize: "12px",
  display: "flex",
  background: "white",
  color: "#0c66c2",
  border: "solid 1px #0c66c2",
  maxWidth: "100%",
  cursor: "pointer",
};
const pending_button = {
  fontWeight: "bold",
  borderRadius: "50px",
  fontSize: "12px",
  display: "flex",
  background: "white",
  color: "gray",
  border: "solid 1px gray",
  maxWidth: "100%",
  cursor: "pointer",
};

export default UserCardRight;
