// import "./Header.css";
// import HeaderOption from "./HeaderOption";
// import SearchIcon from "@material-ui/icons/Search";
// import HomeIcon from "@material-ui/icons/Home";
// import SupervisorAccountIcon from "@material-ui/icons/SupervisorAccount";
// import BusinessCenterIcon from "@material-ui/icons/BusinessCenter";
// import ChatIcon from "@material-ui/icons/Chat";
// import NotificationsIcon from "@material-ui/icons/Notifications";
import { withCookies } from "react-cookie";
import { Link } from "react-router-dom";
import styled from "styled-components";
import Badge from "@material-ui/core/Badge";
import { withStyles } from "@material-ui/core/styles";

import React, { Component } from "react";

const styles = theme => ({
  customBadge: {
    color:"white",
    backgroundColor: "rgb(205, 64, 30)",
  }
});

class Header extends Component {
  constructor(props) {
    super(props);

    this.state = {
      activeHome: true,
      activeNetwork: false,
      loc: "",

      no_of_request: "",
    };
  }

  removeallCookies = () => {
    this.props.cookies.remove("auth-token");
    this.props.cookies.remove("profile");
    this.props.cookies.remove("profile-id");
    this.props.cookies.remove("about-id");
  };
  thisClicked = () => {
    console.log(window.location.pathname);

    console.log(window.location.href.split("/"));
    this.setState({
      loc: window.location.href.split("/")[
        window.location.href.split("/").length - 1
      ],
    });
  };

  componentDidMount() {
    fetch(
      `http://127.0.0.1:8000/uapi/users/${
        this.props.cookies.get("auth-token").user.id
      }/`,
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Token ${this.props.cookies.get("auth-token").token}`,
        },
      }
    )
      .then((resp) => resp.json())
      .then((resp) => {
        console.log(resp);
        this.setState({ no_of_request: resp.waitFollowers.length });
      })
      .catch((error) => console.log(error));
  }

  render() {
    const { classes } = this.props;
    return (
      <Container style={{ position: "fixed" }}>
        <Content>
          <Logo>
            <a href="/">
              <img src="/images/home-logo.svg" alt="" />
            </a>
          </Logo>
          <Search>
            <div>
              <input type="text" placeholder="Search" />
            </div>
            <SearchIcon>
              <img src="/images/search-icon.svg" alt="" />
            </SearchIcon>
          </Search>
          <Nav>
            <NavListWrap>
              <NavList
                className={
                  window.location.pathname.slice(1) == "" ? "active" : null
                }
              >
                <Link to={"/"}>
                  <img
                    onClick={this.thisClicked}
                    src="/images/nav-home.svg"
                    alt=""
                  />
                  <span onClick={this.thisClicked}>Home</span>
                </Link>
              </NavList>

              <NavList>
                <Link to={"/network"}>
                  <Badge
                    classes={{ badge: classes.customBadge }}
                    style={{paddingLeft:"35px"}}
                    badgeContent={this.state.no_of_request}
                  />

                  <img
                    onClick={this.thisClicked}
                    src="/images/nav-network.svg"
                    alt=""
                  />
                  <NavList
                    className={
                      window.location.pathname.slice(1) == "network"
                        ? "active"
                        : null
                    }
                  >
                    <span onClick={this.thisClicked}>My Network</span>
                  </NavList>
                </Link>
              </NavList>

              <NavList
                className={
                  window.location.pathname.slice(1) == "/#" ? "active" : null
                }
              >
                <Link to={"/"}>
                  <img src="/images/nav-jobs.svg" alt="" />
                  <span onClick={this.thisClicked}>Jobs</span>
                </Link>
              </NavList>

              <NavList
                className={
                  window.location.pathname.slice(1) == "/#" ? "active" : null
                }
              >
                <Link to={"/"}>
                  <img src="/images/nav-messaging.svg" alt="" />
                  <span>Messaging</span>
                </Link>
              </NavList>

              <NavList
                className={
                  window.location.pathname.slice(1) == "/#" ? "active" : null
                }
              >
                <Link to={"/"}>
                  <img src="/images/nav-notifications.svg" alt="" />
                  <span>Notifications</span>
                </Link>
              </NavList>

              <User
                className={
                  window.location.pathname.slice(1) == "profile"
                    ? "active"
                    : null
                }
              >
                <Link to={"/"}>
                  <img src="/images/user.svg" alt="" />
                  <span>Me</span>
                  {/* <img src="/images/down-icon.svg" alt="" /> */}
                </Link>

                <SignOut>
                  <Link to={"/home"} onClick={this.removeallCookies}>
                    Sign Out
                  </Link>
                </SignOut>
              </User>

              <Work>
                <Link to={"/"}>
                  <img src="/images/nav-work.svg" alt="" />
                  <span>
                    Work
                    <img src="/images/down-icon.svg" alt="" />
                  </span>
                </Link>
              </Work>
            </NavListWrap>
          </Nav>
        </Content>
      </Container>
      // <div className="header">
      //   <div className="header__left">
      //       <img
      //         src="https://www.flaticon.com/svg/static/icons/svg/174/174857.svg"
      //         alt="linkedin logo"
      //         onClick={() => window.location.href="/"}
      //       />

      //     <div className="header__search">
      //       <SearchIcon style={{ color: "black" }} />
      //       <input type="text" placeholder="Search" />
      //     </div>
      //   </div>

      //   <div className="header__right">
      //     <HeaderOption Icon={HomeIcon} title="Home" />
      //     <HeaderOption Icon={SupervisorAccountIcon} title="My Network" />
