import React, { Component } from "react";
import { Avatar } from "@material-ui/core";
import { withCookies } from "react-cookie";

export class Network extends Component {
  constructor(props) {
    super(props);

    this.state = { followers: [] };
  }
  componentDidMount() {
    if (this.props.cookies.get("auth-token").user.followers.length > 0) {
      for (
        var i = 0;
        i < this.props.cookies.get("auth-token").user.followers.length;
        i++
      ) {
        fetch(
          `http://127.0.0.1:8000/uapi/users/${
            this.props.cookies.get("auth-token").user.followers[i].user
          }/`,
          {
            method: "GET",
            headers: {
              "Content-Type": "application/json",
              Authorization: `Token ${
                this.props.cookies.get("auth-token").token
              }`,
            },
          }
        )
          .then((resp) => resp.json())
          .then((resp) => {
            console.log(
              this.props.cookies.get("auth-token").user.followers[i].user
            );
            var joined = this.state.followers.concat([resp]);
            this.setState({ followers: joined });
          })
          .catch((error) => console.log(error));
      }
    }
  }

  render() {
    console.log(this.state.followers);
    console.log(this.props.cookies.get("auth-token"));

    return (
      <div className="profile">
        <div className="profile__top">
          {this.state.followers.map((user) => (
            <div className="user">
              <div className="user__header">
                {user.profile_pic ? (
                  <Avatar src={user.profile_pic} alt="Profile" />
                ) : (
                  <Avatar src="/images/user.svg" alt="Profile" />
                )}
                <span className="ml-2" style={{ fontWeight: "bold" }}>
                  {user.username}
                </span>
              </div>
              <hr />
            </div>
          ))}
        </div>
      </div>
    );
  }
}

export default withCookies(Network);
