import React, { Component } from "react";
import { Button, TextField } from "@material-ui/core";
import { Modal } from "react-bootstrap";
import { withCookies } from "react-cookie";
import { Autocomplete } from "@material-ui/lab";

class Skills extends Component {
  constructor(props) {
    super(props);

    this.state = {
      modalShow: true,
      form: {
        skills: null,
      },
    };
  }

  handle = (name, option) => {
    this.setState({
      form: {
        ...this.state.form,
        [name]: option,
      },
    });
  };

  render() {
    return (
      <div>
        <Modal
          show={this.state.modalShow}
          size="large"
          aria-labelledby="contained-modal-title-vcenter"
          //   centered
          onHide={() => this.setState({ modalShow: false })}
          style={{ background: "rgba(0,0,0,0.3)" }}
          className="fade"
        >
          <Modal.Header closeButton>Add Skills</Modal.Header>

          <Modal.Body>
            <Autocomplete
            //   multiple
            //   id="tags-standard"
              options={skillsName}
              getOptionLabel={(option) => option.name}
              //   defaultValue={[s[13]]}
              onChange={(event, value, reason) => {
                this.handle("skills", value);
              }}
              renderInput={(params) => (
                <TextField
                  {...params}
                  variant="standard"
                  label="Skills"
                  placeholder="Favorites"
                  value={this.state.skill}
                />
              )}
            />
            <p>
              Value selected :{" "}
              {this.state.form.skills ? this.state.form.skills.name : null}
            </p>
            <Modal.Footer>
              <Button
                // onClick={this.deleteLicense}
                style={save_button}
                type="submit"
              >
                Add
              </Button>
            </Modal.Footer>
          </Modal.Body>
        </Modal>
      </div>
    );
  }
}

const save_button = {
  paddingLeft: "20px",
  paddingRight: "20px",
  marginTop: "10px",
  marginLeft: "10px",
  fontWeight: "bold",
  borderRadius: "50px",
  display: "flex",
  background: "#0c66c2",
  color: "white",
  border: "solid 1px #0c66c2",
};

const skillsName = [
  { name: "HTML" },
