import React, { Component } from "react";
import DeleteIcon from "@material-ui/icons/Delete";
import { Modal } from "react-bootstrap";
import CameraAltRoundedIcon from "@material-ui/icons/CameraAltRounded";
import { Avatar, Button } from "@material-ui/core";
import "./AddProfilePic.css";
import { withCookies } from "react-cookie";

export class AddProfilePic extends Component {
  constructor(props) {
    super(props);

    this.state = {
      modalShow: true,
      modalDelete: false,
      profile_pic: this.props.profile_pic,
      profile_picAsFile: this.props.profile_picAsFile,
    };
  }

  removeImage = () => {
    this.setState({
      profile_pic: "",
      profile_picAsFile: null,
      modalDelete: true,
    });
  };

  handleProfilePic = () => {
    // console.log(window.location.origin + "/images/user.svg");
    var form_data = new FormData();

    if (this.state.profile_picAsFile) {
      form_data.set(
        "profile_pic",
        this.state.profile_picAsFile,
        this.state.profile_picAsFile.name
      );
    }
    console.log(this.props.cookies.get("auth-token").token);
    fetch(
      `${process.env.REACT_APP_API_URL}/uapi/users/${
        this.props.cookies.get("auth-token").user.id
      }/update_user/`,
      {
        method: "PUT",
        headers: {
          Authorization: `Token ${this.props.cookies.get("auth-token").token}`,
        },
        body: form_data,
      }
    )
      .then((res) => res.json())
      .then((res) => {
        this.setState({ modalShow: false });
        // window.location.reload();
        console.log("response", res);
      })
      .catch((err) => console.log(err));
  };

  deleteProfilePic = () => {};

  render() {
    return (
      <div className="p-auto">
        <Modal
          className="my-modal"
          style={{
            height: "700px",
            color: "white",
          }}
          scrollable={true}
          show={this.state.modalShow}
          size="md"
          aria-labelledby="contained-modal-title-vcenter"
          centered
          onHide={() => this.setState({ modalShow: false })}
        >
          <Modal.Header
            closeButton
            autoFocus
            style={{ borderBottom: "0 none" }}
            // onClick={() => this.setState({ modalShow: false })}
          >
            <Modal.Title
              style={{
                fontSize: "1.2rem",
                color: "white",
              }}
              id="contained-modal-title-vcenter"
            >
              Profile Photo
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Avatar
              className="m-auto"
              style={{ width: "250px", height: "250px", borderRadius: "50%" }}
              src={this.state.profile_pic}
              alt="Profile"
            />
          </Modal.Body>

          <Modal.Footer style={{ borderTopColor: "gray" }}>
            <input
              style={{ display: "none" }}
              type="file"
              id="file"
              onChange={(e) =>
                this.setState(
                  {
                    profile_pic: URL.createObjectURL(e.target.files[0]),
                    profile_picAsFile: e.target.files[0],
                  },
                  function () {
                    console.log(
                      "file",
                      e.target.files[0],
                      URL.createObjectURL(e.target.files[0])
                    );
                  }
                )
              }
            />
            <label htmlFor="file">
              <CameraAltRoundedIcon
                className="ml-4"
                style={{ color: "white", marginLeft: "10px" }}
              />
              <div>
                <span>Add Photo</span>
              </div>
            </label>

            <div className="mr-auto ml-3">
              <DeleteIcon
                className="ml-2"
                style={{ color: "white" }}
                onClick={this.removeImage}
              />
              <div>
                <span>Delete</span>
              </div>
            </div>
            <Button onClick={this.handleProfilePic} style={post_button}>
              Upload Photo
            </Button>
          </Modal.Footer>
        </Modal>
        {this.state.deleteModal ? (
          <Modal
            show={this.state.deleteModal}
            size="sm"
            aria-labelledby="contained-modal-title-vcenter"
            onHide={() => this.setState({ deleteModal: false })}
            style={{
              background: "rgba(0,0,0,0.3)",
            }}
            className="fade"
          >
            <Modal.Header style={{ margin: "auto", borderBottom: "0 none" }}>
              Delete Post?
            </Modal.Header>

            <Modal.Body
              style={{
                padding: "0px",
                borderRadius: "60px",
              }}
            >
              <div className="m-auto p-2 pb-3">
                <span
                  style={{
                    fontSize: "0.8rem",
                    lineHeight: "0.5",
                    color: "rgba(0,0,0,0.6)",
                  }}
                >
                  Are you sure you want to permanently remove this post from
                  LinkedIn?
                </span>
              </div>
              <Modal.Footer style={{ padding: "0px", paddingBottom: "5px" }}>
                <Button
                  onClick={() => this.setState({ deleteModal: false })}
                  style={cancel_button}
                  type="submit"
                >
                  Cancel
                </Button>
                <Button
                  onClick={this.handleDelete}
                  style={delete_button}
                  type="submit"
                >
                  Delete
                </Button>
              </Modal.Footer>
            </Modal.Body>
          </Modal>
        ) : null}
      </div>
    );
  }
}
const post_button = {
  marginTop: "-10px",
  paddingLeft: "10px",
  paddingRight: "10px",
  marginLeft: "10px",
  fontWeight: "bold",
  borderRadius: "50px",
  fontSize: "15px",
  // display: "flex",
  background: "#0c66c2",
  color: "white",
  border: "solid 1px #0c66c2",
  //   width: "110px",
};

export default withCookies(AddProfilePic);
