import React, { Component } from "react";
import { Modal, Row, Col, ListGroup } from "react-bootstrap";
import { Avatar } from "@material-ui/core";
import "./Count.css";

class Count extends Component {
  constructor(props) {
    super(props);

    this.state = {
      user: "",
      show: false,
      users: [],
      showLike: false,
      showComment: false,
    };
  }

  componentDidMount() {
    for (var i = 0; i < this.props.likes.length; i++) {
      console.log(this.props.likes[i].user);
      fetch(`http://127.0.0.1:8000/uapi/users/${this.props.likes[i].user}/`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Authorization: "Token d194a979f94b27587f743071a64f8d5c7d3dfb01",
        },
      })
        .then((resp) => resp.json())
        // .then((resp) => console.log(resp.username))
        .then((resp) => {
          var joined = this.state.users.concat([resp]);
          this.setState({ users: joined }, console.log("joined", joined));
        })
        .catch((error) => console.log(error));
    }
  }

  modalShowLike = () => {
    for (var i = 0; i < this.props.likes.length; i++) {
      console.log(this.props.likes[i].user);
      fetch(`http://127.0.0.1:8000/uapi/users/${this.props.likes[i].user}/`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Authorization: "Token d194a979f94b27587f743071a64f8d5c7d3dfb01",
        },
      })
        .then((resp) => resp.json())
        // .then((resp) => console.log(resp.username))
        .then((resp) => {
          var joined = this.state.users.concat([resp]);
          this.setState({ users: joined }, console.log("joined", joined));
        })
        .catch((error) => console.log(error));
    }
    this.setState({ show: true, showLike: true });
  };
  modalShowComment = () => {
    for (var i = 0; i < this.props.comments.length; i++) {
      console.log(this.props.comments[i].user);
      fetch(
        `http://127.0.0.1:8000/uapi/users/${this.props.comments[i].user}/`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Authorization: "Token d194a979f94b27587f743071a64f8d5c7d3dfb01",
          },
        }
      )
        .then((resp) => resp.json())
        // .then((resp) => console.log(resp.username))
        .then((resp) => {
          var joined = this.state.users.concat([resp]);
          this.setState({ users: joined }, console.log("joined", joined));
        })
        .catch((error) => console.log(error));
    }
    this.setState({ show: true, showComment: true });
  };

  render() {
    const { likes, comments, no_of_like, no_of_comment } = this.props;
    console.log(likes);

    return (
      <div>
        <button onClick={this.modalShowLike}>{no_of_like}</button>
        <button onClick={this.modalShowComment}>{no_of_comment}</button>

        {this.state.show ? (
          <Modal
            style={{
              height: "500px",
            }}
            scrollable={true}
            show={this.state.show}
            size="md"
            aria-labelledby="contained-modal-title-vcenter"
            centered
          >
            <Modal.Header
              closeButton
              onClick={() => {
                this.setState({
                  show: false,
                  showComment: false,
                  showLike: false,
                });
              }}
            >
              <Modal.Title id="contained-modal-title-vcenter">
                Likes
              </Modal.Title>
            </Modal.Header>
            <Modal.Body>
              {this.state.users.map((user) => (
                <div className="user">
                  <div className="user__header">
                    {user.profile_pic ? (
                      <Avatar src={user.profile_pic} alt="Profile" />
                    ) : (
                      <Avatar
                        src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTP9xCw-TO3d5DvvvTaUE2dx6VLYNO52xxG5A&usqp=CAU"
                        alt="Profile"
                      />
                    )}
                    <span className="ml-2" style={{ fontWeight: "bold" }}>
                      {user.username}
                    </span>
                  </div>
                  <hr />
                </div>
              ))}
            </Modal.Body>
            {/* <Modal.Footer></Modal.Footer> */}
          </Modal>
        ) : null}
      </div>
    );
  }
}

export default Count;
