import React, { Component } from "react";
import { Button, TextField } from "@material-ui/core";
import { Modal } from "react-bootstrap";
import { withCookies } from "react-cookie";

class Education extends Component {
  constructor(props) {
    super(props);

    this.state = {
      modelShow: true,
      credentials: {
        school: "",
        degree: "",
        field_of_study: "",
        start_year: "",
        end_year: "",
        user: this.props.cookies.get("auth-token").user.id,
      },
    };
  }

  inputChanged = (event) => {
    console.log(event.target.value);
    let cred = this.state.credentials;
    cred[event.target.name] = event.target.value;
    this.setState({ credentials: cred });
  };

  createEducation = (e) => {
    fetch(`${process.env.REACT_APP_API_URL}/profile/education/`, {
      method: "POST",
      headers: {
        "Content-type": "application/json",
        Authorization: `Token ${this.props.cookies.get("auth-token").token}`,
      },
      body: JSON.stringify(this.state.credentials),
    })
      .then((resp) => resp.json())
      .then((resp) => {
        console.log(resp);
        this.props.cookies.set("education-id", resp.id);
        // this.props.updateAbout();
      })
      .catch((error) => console.log(error));

    e.preventDefault();
    this.props.onEducationModal(false);
  };

  editEducation = (e) => {
    fetch(
      `${
        process.env.REACT_APP_API_URL
      }/profile/education/${this.props.cookies.get("education-id")}/`,
      {
        method: "PUT",
        headers: {
          "Content-type": "application/json",
          Authorization: `Token ${this.props.cookies.get("auth-token").token}`,
        },
        body: JSON.stringify(this.state.credentials),
      }
    )
      .then((resp) => resp.json())
      .then((resp) => {
        console.log(resp);
        this.setState({ modelShow: false });
        //   this.props.updateAbout();
      })
      .catch((error) => console.log(error));

    e.preventDefault();
    this.props.onEducationModal(false);
  };

  getEducationData = () => {
    fetch(
      `${process.env.REACT_APP_API_URL}/profile/user_education/${this.props.education_id}/`,
      {
        method: "GET",
        headers: {
          "Content-type": "application/json",
        },
      }
    )
      .then((resp) => resp.json())
      .then((resp) => {
        // console.log(resp)
        if (resp.user_education) {
          this.setState({
            credentials: {
              school: resp.user_education.school,
              degree: resp.user_education.degree,
              field_of_study: resp.user_education.field_of_study,
              start_year: resp.user_education.start_year,
              end_year: resp.user_education.end_year,
              about: resp.user_education.about,
              user: this.props.cookies.get("auth-token").user.id,
            },
          });
        }
      })
      .catch((error) => console.log(error));
  };

  UNSAFE_componentWillMount() {
    if (!this.props.onCreateEducation) {
      this.getEducationData();
    }
  }

  render() {
    return (
      <Modal
        show={this.state.modelShow}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
        onHide={() => this.setState({ modelShow: false })}
      >
        <Modal.Header closeButton>Edit Education</Modal.Header>
        <Modal.Body className="profile__modal">
          {/* School */}
          <TextField
            rowsMax={4}
            rowsMin={4}
            variant="outlined"
            size="large"
            type="text"
            fullWidth
            id="school"
            value={this.state.credentials.school}
            label="School"
            name="school"
            onChange={this.inputChanged}
            required
            className="mb-3"
          />
          {/* Degree */}
          <TextField
            rowsMax={4}
            rowsMin={4}
            variant="outlined"
            size="large"
            type="text"
            fullWidth
            id="degree"
            value={this.state.credentials.degree}
            label="Degree"
            name="degree"
            onChange={this.inputChanged}
            required
            className="mb-3"
          />
          {/* Field of study */}
          <TextField
            rowsMax={4}
            rowsMin={4}
            variant="outlined"
            size="large"
            type="text"
            fullWidth
            id="field_of_study"
            value={this.state.credentials.field_of_study}
            label="Field of study"
            name="field_of_study"
            onChange={this.inputChanged}
            required
            className="mb-3"
          />
          {/* start year and end year */}
          <div style={{ display: "flex  " }}>
            <TextField
              className="mt-3 mb-5 mr-2"
              variant="outlined"
              size="small"
              type="text"
              fullWidth
              id="start_year"
              value={this.state.credentials.start_year}
              label="Start Year"
              name="start_year"
              onChange={this.inputChanged}
              required
            />
            <TextField
              className="mt-3 mb-5 "
              variant="outlined"
              size="small"
              type="text"
              fullWidth
              id="end_year"
              value={this.state.credentials.end_year}
              label="End Year"
              name="end_year"
              onChange={this.inputChanged}
              required
            />
          </div>
        </Modal.Body>
        <Modal.Footer>
          {this.props.onCreateEducation ? (
            <Button
              onClick={this.createEducation}
              style={save_button}
              type="submit"
            >
              Save
            </Button>
          ) : (
            <Button
              onClick={this.editEducation}
              style={save_button}
              type="submit"
            >
              save1
            </Button>
          )}
        </Modal.Footer>
      </Modal>
    );
  }
}

const save_button = {
  paddingLeft: "20px",
  paddingRight: "20px",
  marginTop: "10px",
  marginLeft: "10px",
  fontWeight: "bold",
  borderRadius: "50px",
  display: "flex",
  background: "#0c66c2",
  color: "white",
  border: "solid 1px #0c66c2",
};

export default withCookies(Education);
