import React, { Component } from "react";

export class AddProfilePic extends Component {
  render() {
    return <div></div>;
  }
}
<Modal
  style={{
    height: "700px",
  }}
  scrollable={true}
  show={this.state.modalPost}
  size="md"
  aria-labelledby="contained-modal-title-vcenter"
  centered
>
  <Modal.Header
    closeButton
    autoFocus
    onClick={() => this.setState({ modalPost: false })}
  >
    <Modal.Title
      style={{
        fontSize: "1.2rem",
      }}
      id="contained-modal-title-vcenter"
    >
      Create a Post
    </Modal.Title>
  </Modal.Header>
  <Modal.Body>
    <Form.Group controlId="exampleForm.ControlTextarea1">
      <Form.Label>
        <div className="post__header">
          {this.props.cookies.get("auth-token").user.profile_pic ? (
            <Avatar
              src={this.props.cookies.get("auth-token").user.profile_pic}
              alt="Profile"
            />
          ) : (
            <Avatar
              className="post__image"
              src="/images/user.svg"
              alt="Profile"
            />
          )}
          <span className="ml-2" style={{ fontWeight: "bold" }}>
            {this.props.cookies.get("auth-token").user.username}
          </span>
        </div>
      </Form.Label>
      <Form.Control
        style={{ border: "none" }}
        placeholder="What do you want to talk about?"
        onChange={this.handleBody}
        as="textarea"
        rows={3}
      />
    </Form.Group>

    {this.state.image ? (
      <React.Fragment>
        <div className="img_wrp">
          <img
            className="post__image"
            style={{ borderRadius: "10px" }}
            src={this.state.image}
          />
          <AiFillCloseCircle
            className="close__image"
            onClick={this.removeImage}
          />
          {/* <Icon  /> */}
        </div>
      </React.Fragment>
    ) : null}
  </Modal.Body>
  <Modal.Footer>
    <input
      style={{ display: "none" }}
      type="file"
      id="file"
      onChange={(e) =>
        this.setState({
          image: URL.createObjectURL(e.target.files[0]),
          imageAsFile: e.target.files[0],
        })
      }
    />
    <label className="mr-auto" htmlFor="file">
      <InsertPhotoIcon style={{ color: "gray" }} />
    </label>
    <Button
      style={post_button}
      disabled={this.state.body.length < 1}
      type="submit"
      onClick={this.submitPost}
    >
      Post
    </Button>
  </Modal.Footer>
</Modal>;

export default AddProfilePic;
