import React, { Component } from "react";

export class UserList extends Component {
  constructor(props) {
    super(props);

    this.state = {
      users: [],
    };
  }

  componentDidMount() {
    fetch(
      `http://127.0.0.1:8000/uapi/userDetail/${
        this.props.cookies.get("auth-token").user.id
      }/`,
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Token ${this.props.cookies.get("auth-token").token}`,
        },
      }
    )
      .then((resp) => resp.json())
      .then((resp) => {
        var joined = this.state.users.concat([resp]);
        this.setState({ users: joined });
      })
      .catch((error) => console.log(error));
  }

  render() {
    return (
      <div className="mt-4">
        {this.state.waitFollowers.map((user) => (
          <div className="user">
            <div className="user__header" style={{ display: "flex" }}>
              {user.profile_pic ? (
                <Avatar
                  src="http://127.0.0.1:8000/media/profile_images/user.svg"
                  alt="Profile"
                />
              ) : (
                <Avatar src="/images/user.svg" alt="Profile" />
              )}
