import React, { Component } from "react";

class Education extends Component {

    constructor(props) {
        super(props)

        this.state = {
            modelShow: true,
            credentials: {
                school: "",
                degree:"",
                field_of_study : "",
                start_year : "",
                end_year : "",
                user: this.props.cookies.get("auth-token").user.id,
              },
        }
    }


  render() {
    return (
      <Modal
        show={this.state.modelShow}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
        onHide={() => this.setState({ modelShow: false })}
      >
        <Modal.Header closeButton>Edit About</Modal.Header>
        <Modal.Body className="profile__modal">
          {/* About */}
          <TextareaAutosize
            rowsMax={4}
            rowsMin={4}
            variant="outlined"
            size="large"
            type="text"
            fullWidth
            id="about"
            value={this.state.credentials.about}
            label="About"
            name="about"
            onChange={this.inputChanged}
            required
            className="mb-3"
          />
        </Modal.Body>
        <Modal.Footer>
          {this.props.cookies.get("about-id") ? (
            <Button
              onClick={this.editProfile}
              style={save_button}
              type="submit"
            >
              save
            </Button>
          ) : (
            <Button
              onClick={this.createProfile}
              style={save_button}
              type="submit"
            >
              Save
            </Button>
          )}
        </Modal.Footer>
      </Modal>
    );
  }
}

export default Education;
