import React, { Component } from "react";
import { Modal } from "react-bootstrap";
class Count extends Component {
  constructor(props) {
    super(props);

    this.state = {
      user: "",
      show: false,
    };
  }

  componentDidMount() {
    this.LikeInfo(this.props);
  }

  LikeInfo = (id) => {
    fetch(`http://127.0.0.1:8000/uapi/users/${id}/`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Token d194a979f94b27587f743071a64f8d5c7d3dfb01",
      },
    })
      .then((resp) => resp.json())
      .then((res) =>
        this.setState(
          {
            user: res.username,
            show: true,
          },
          console.log(this.state.user)
        )
      )
      .catch((error) => console.log(error));
  };
  LikeInfo = () => {
    this.setState({ show: true });
  };

  render() {
    const { likes, comments, no_of_like, no_of_comment } = this.props;

    return (
      <div>
        {likes.map(
          (like) => (
            function () {
              this.setState({ user: like.user });
            },
            (
              <div>
                <button>{no_of_like}</button>
              </div>
            )
          )
        )}
        {this.state.show ? (
          <Modal
            show={this.state.show}
            size="md"
            aria-labelledby="contained-modal-title-vcenter"
            centered
          >
            <Modal.Header
              closeButton
              onClick={() => {
                this.setState({ show: false });
              }}
            >
              <Modal.Title id="contained-modal-title-vcenter">
                Reactions
              </Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <h4>{this.state.user}</h4>
            </Modal.Body>
            {/* <Modal.Footer></Modal.Footer> */}
          </Modal>
        ) : null}
      </div>
    );
  }
}

export default Count;
