import React, { Component } from "react";
import { Button, TextField } from "@material-ui/core";
import { Modal } from "react-bootstrap";
import { withCookies } from "react-cookie";
import { Autocomplete } from "@material-ui/lab";

class Skills extends Component {
  constructor(props) {
    super(props);

    this.state = {
      modalShow: true,
      skills: [],
      user: this.props.cookies.get("auth-token").user.id,
    };
  }

  handle = (e, v) => {
    console.log(v);
    this.setState({
      skills: v,
    });
  };

  AddSkill = (e) => {
    for (let index = 0; index < this.state.skills.length; index++) {
      console.log(this.state.skills);
      const cred = fetch(`${process.env.REACT_APP_API_URL}/profile/skills/`, {
        method: "POST",
        headers: {
          "Content-type": "application/json",
          Authorization: `Token ${this.props.cookies.get("auth-token").token}`,
        },
        body: JSON.stringify({
          skill: this.state.skills[index]["skill"],
          user: this.state.user,
        }),
      })
        .then((resp) => resp.json())
        .then((resp) => {
          console.log(resp);
          //   this.props.cookies.set("license-id", resp.id);
          //   this.props.updateLicense();
        })
        .catch((error) => console.log(error));

      e.preventDefault();
      // this.props.onLicenseModal(false);
    }
  };

  render() {
    return (
      <div>
        <Modal
          show={this.state.modalShow}
          size="large"
          aria-labelledby="contained-modal-title-vcenter"
          //   centered
          onHide={() => this.setState({ modalShow: false })}
          style={{ background: "rgba(0,0,0,0.3)" }}
          className="fade"
        >
          <Modal.Header closeButton>Add Skills</Modal.Header>
          <Modal.Body>
            <Autocomplete
              multiple
              id="tags-standard"
              options={skillsName}
              getOptionLabel={(option) => option.skill}
              onChange={this.handle}
              renderInput={(params) => (
                <TextField
                  {...params}
                  variant="standard"
                  label="Skills"
                  placeholder="Add Skill"
                  value={this.state.skills}
                />
              )}
            />
            <Modal.Footer>
              <Button onClick={this.AddSkill} style={save_button} type="submit">
                Add
              </Button>
            </Modal.Footer>
          </Modal.Body>
        </Modal>
      </div>
    );
  }
}

const save_button = {
  paddingLeft: "20px",
  paddingRight: "20px",
  marginTop: "10px",
  marginLeft: "10px",
  fontWeight: "bold",
  borderRadius: "50px",
  display: "flex",
  background: "#0c66c2",
  color: "white",
  border: "solid 1px #0c66c2",
};

const skillsName = [
  { skill: "HTML" },
