// import "./Widgets.css"
// import InfoOutlinedIcon from '@material-ui/icons/InfoOutlined';
// import FiberManualRecordIcon from '@material-ui/icons/FiberManualRecord';
import styled from "styled-components";

import React, { Component } from 'react'

class Widgets extends Component {
s
    newsArticle = (heading, subtitle) => (
        <div className="widgets__article">
            <div className="widgets__articleLeft">
                <FiberManualRecordIcon className="mr-1"/>
            </div>

            <div className="widgets__articleRight">
                <h4>{heading}</h4>
                <p>{subtitle}</p>
            </div>
        </div>
    );

    render() {
        return (
            <Container>
            <FollowCard>
              <Title>
                <h2>Add to your feed</h2>
                <img src="/images/feed-icon.svg" alt="" />
              </Title>

              <FeedList>
                <li>
                  <a>
                    <Avatar />
                  </a>
                  <div>
                    <span>#Linkedin</span>
                    <button>Follow</button>
                  </div>
                </li>
                <li>
                  <a>
                    <Avatar />
                  </a>
                  <div>
                    <span>#Video</span>
                    <button>Follow</button>
                  </div>
                </li>
              </FeedList>

              <Recommendation>
                View all recommendations
                <img src="/images/right-icon.svg" alt="" />
              </Recommendation>
            </FollowCard>
            <BannerCard>
              <img
                src="https://static-exp1.licdn.com/scds/common/u/images/promo/ads/li_evergreen_jobs_ad_300x250_v1.jpg"
                alt=""
              />
            </BannerCard>
          </Container>
        //     <div className="widgets">
        //     <div className="widgets__header">
        //         <h2>Linkedin News</h2>
        //         <InfoOutlinedIcon />
        //     </div>
        //     {this.newsArticle("Elon Musk is now the richest person in the world", "Top news - 4296 readers")}
        //     {this.newsArticle("Study suggests Pfizer vaccine works against virus variant", "Top news - 3126 readers")}
        //     {this.newsArticle("YouTube Suspends Trump’s Channel for at Least Seven Days", "Top news - 2926 readers")}
        //     {this.newsArticle("EHang’s passenger-carrying drone a key step in Urban Air Mobility", "Top news - 4231 readers")}

        //     <img src="https://static-exp1.licdn.com/scds/common/u/images/promo/ads/li_evergreen_jobs_ad_300x250_v1.jpg" alt="ad"/>
        // </div>
        )
    }
}

const Container = styled.div`
  grid-area: rightside;
`;

const FollowCard = styled.div`
  text-align: center;
  overflow: hidden;
  margin-bottom: 8px;
  background-color: #fff;
  border-radius: 5px;
  position: relative;
  border: none;
  box-shadow: 0 0 0 1px rgb(0 0 0 / 15%), 0 0 0 rgb(0 0 0 / 20%);
  padding: 12px;
`;

const Title = styled.div`
  display: inline-flex;
  align-items: center;
  justify-content: space-between;
  font-size: 16px;
  width: 100%;
  color: rgba(0, 0, 0, 0.6);
`;

const FeedList = styled.ul`
  margin-top: 16px;
  li {
    display: flex;
    align-items: center;
    margin: 12px 0;
    position: relative;
    font-size: 14px;
    & > div {
      display: flex;
      flex-direction: column;
    }
    button {
      background-color: transparent;
      color: rgba(0, 0, 0, 0.6);
      box-shadow: inset 0 0 0 1px rgba(0, 0, 0, 0.6);
      padding: 16px;
      align-items: center;
      border-radius: 15px;
      box-sizing: border-box;
      font-weight: 600;
      display: inline-flex;
      justify-content: center;
      max-height: 32px;
      max-width: 480px;
      text-align: center;
      outline: none;
    }
  }
`;

const Avatar = styled.div`
  background-image: url("https://static-exp1.licdn.com/sc/h/1b4vl1r54ijmrmcyxzoidwmxs");
  background-size: contain;
  background-position: center;
  background-repeat: no-repeat;
  width: 48px;
  height: 48px;
  margin-right: 8px;
`;

const Recommendation = styled.a`
  color: #0a66c2;
  display: flex;
  align-items: center;
  font-size: 14px;
`;

const BannerCard = styled(FollowCard)`
  img {
    width: 100%;
    height: 100%;
  }
`;

export default Widgets
