import "./Register.css";
import React, { Component } from "react";
import { Container, Form, Row, Card } from "react-bootstrap";
import { Typography, Button } from "@material-ui/core";
import logo from "./google.png";
import { Link } from "react-router-dom";

class Login extends Component {
  constructor(props) {
    super(props);

    this.state = {
      credentials: {
        // user credentials
        username: "",
        email: "",
        password: "",
      },
    };
  }

  inputChanged = (event) => {
    let cred = this.state.credentials;
    cred[event.target.name] = event.target.value;
    this.setState({ credentials: cred });
  };

  componentDidMount() {
    fetch("http://127.0.0.1:8000/uapi/users/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((resp) => resp.json())
      .then((resp) => console.log(resp))
      .catch((error) => console.log(error));
  }

  render() {
    return (
      <Container fluid style={{ backgroundColor: "#f3f2ef" }}>
        <Row>
          <img
            className="linkedin-img-reg rounded mx-auto d-block"
            src="https://www.logo.wine/a/logo/LinkedIn/LinkedIn-Logo.wine.svg"
            alt="linkedin logo"
          />
        </Row>
        <Typography align="center" variant="h4" style={{ marginTop: "-80px" }}>
          Make the most of your professional life
        </Typography>
        <Row
          style={{
            display: "flex",
            justifyContent: "center",
            marginTop: "30px",
            paddingBottom: "10%",
          }}
        >
          <Card className="p-3" style={{ width: "20rem" }}>
            <Form>
              <Form.Group controlId="formBasicText">
                <Form.Label>Full Name</Form.Label>
                <Form.Control
                  value={this.state.credentials.username}
                  size="sm"
                  type="text"
                  placeholder="Enter FullName"
                  onChange={this.inputChanged}
                />
              </Form.Group>
              <Form.Group controlId="formBasicEmail">
                <Form.Label>Email</Form.Label>
                <Form.Control
                  value={this.state.credentials.email}
                  size="sm"
                  type="email"
                  placeholder="Enter email"
                  onChange={this.inputChanged}
                />
              </Form.Group>

              <Form.Group controlId="formBasicPassword">
                <Form.Label>Password</Form.Label>
                <Form.Control
                  value={this.state.credentials.password}
                  size="sm"
                  type="password"
                  placeholder="Password"
                  onChange={this.inputChanged}
                />
              </Form.Group>
              <Button
                variant="contained"
                style={{
                  borderRadius: "50px",
                  background: "#0c66c2",
                  color: "#FFFFFF",
                  minWidth: "100%",
                }}
              >
                Agree & Join
              </Button>
              <span className="hr-sect" style={{ color: "#000" }}>
                or
              </span>
              <Button
                variant="contained"
                style={{
                  borderRadius: "50px",
                  background: "#FFFFFF",
                  color: "#0c66c2",
                  minWidth: "100%",
                  fontWeight: "bold",
                  fontSize: "17px",
                }}
              >
                <img src={logo} className="pr-3" />
                Join with Google
              </Button>
            </Form>
            <Typography align="center" className="mt-3">
              Already on LinkedIn?
              <Link to={"/login"}>
                <span
                  className="ml-1"
                  style={{ color: "#0c66c2", fontWeight: "bold" }}
                >
                  Sign In
                </span>
              </Link>
            </Typography>
          </Card>
        </Row>
      </Container>
    );
  }
}

export default Login;
