import React, { Component } from "react";
import { Avatar } from "@material-ui/core";
import { Modal } from "react-bootstrap";

class ShowFollowers extends Component {
  constructor(props) {
    super(props);

    this.state = {
      modalShow: true,
      followers: [],
    };
  }

  onHide = () => {
    this.setState({ modelShow: false });
    this.props.onChangeFollowers(false);
  };

  componentDidMount() {
    if (this.props.followers && this.props.followers.length > 0) {
      for (let index = 0; index < this.props.followers.length; index++) {
        fetch(
          `${process.env.REACT_APP_API_URL}/uapi/userDetail/${this.props.followers[index].user_id}/`,
          {
            method: "GET",
            headers: {
              "Content-Type": "application/json",
            },
          }
        )
          .then((resp) => resp.json())
          .then((resp) => {
            var joined = this.state.followers.concat([resp]);
            this.setState({ followers: joined });
          });
      }
    }
  }

  render() {
    return (
      <div>
        <Modal
          show={this.state.modalShow}
          size="md"
          aria-labelledby="contained-modal-title-vcenter"
          centered
          onHide={this.onHide}
        >
          <Modal.Header closeButton>Edit About</Modal.Header>
          <Modal.Body className="profile__modal">
            {this.state.followers.map((follower) => (
              <div style={{ display: "flex",padding:"10px" }}>
                {follower.profile_pic ? (
                  <Avatar src={follower.profile_pic} alt="Profile" />
                ) : (
                  <Avatar src="/images/user.svg" alt="Profile" />
                )}
                <span className="ml-2 " style={{ fontWeight: "bold" }}>
                  {follower.username}
                </span>
              </div>
            ))}
          </Modal.Body>
        </Modal>
      </div>
    );
  }
}

export default ShowFollowers;
