import React, { Component } from "react";
class Count extends Component {
  constructor(props) {
    super(props);

    this.state = {
      user: "",
    };
  }

  componentDidMount() {}

  LikeInfo = (id) => {
    fetch(`http://127.0.0.1:8000/uapi/users/${id}/`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Token d194a979f94b27587f743071a64f8d5c7d3dfb01",
      },
    })
      .then((resp) => resp.json())
      .then((res) =>
        this.setState(
          {
            user: res.username,
          },
          console.log(this.state.user)
        )
      )
      .catch((error) => console.log(error));
  };

  render() {
    const { likes, comments, no_of_like, no_of_comment } = this.props;
    console.log(no_of_like);
    return (
      <div>
        {likes.map((like) => (
          <div>
            <a onClick={this.LikeInfo(like.user)} className="button">
              {no_of_like}
            </a>
            {/* <a className="button mt-1">{no_of_comment}</a> */}
          </div>
        ))}
      </div>
    );
  }
}

export default Count;
