      //       alt="background"
      //     />
      //     {this.props.user.profile_pic ? (
      //       <Avatar
      //         src="http://127.0.0.1:8000/media/profile_images/user.svg"
      //         alt="Profile"
      //       />
      //     ) : (
      //       <Avatar src="/images/user.svg" alt="Profile" />
      //     )}
      //     <Link to={"/profile"}>
      //       <span style={{ fontWeight: "bold" }}>
      //         {this.props.user.username}
      //       </span>
      //     </Link>

      //     <Button
      //       onClick={this.submitFollow}
      //       className="ml-2"
      //       style={save_button}
      //     >
      //       Connect
      //     </Button>
      //   </div>
      // </div>

      <div className="breakpoint">
        <Card style={{ padding: "5px", border: "white" }}>
          <div className="list__top">
            <img src={this.props.user.cover_pic} alt="background" />
            <AiFillCloseCircle
              className="over__image"
              onClick={this.removeImage}
            />
            {/* <Icon  /> */}

            {this.props.user.profile_pic ? (
              <Avatar
                style={{ width: "80px", height: "80px" }}
                src="http://127.0.0.1:8000/media/profile_images/user.svg"
                alt="Profile"
              />
            ) : (
              <Avatar
                style={{ width: "80px", height: "80px" }}
                src="/images/user.svg"
                alt="Profile"
              />
            )}
            <Link to={"/profile"}>
              <span
                style={{ fontWeight: "bold", color: "black", fontSize: "18px" }}
              >
                {this.props.user.username}
              </span>
            </Link>

            <Button
              onClick={this.submitFollow}
              className="but mt-3"
              style={save_button}
            >
              {this.state.pending ? Pending : Connect}
            </Button>
          </div>
        </Card>
      </div>
    );
  }
}
const save_button = {
  fontWeight: "bold",
  borderRadius: "50px",
  fontSize: "12px",
  display: "flex",
  background: "white",
  color: "#0c66c2",
  border: "solid 1px #0c66c2",
  maxWidth: "100%",
  cursor: "pointer",
};
export default withCookies(UserCards);
