import React, { Component } from "react";
import { Button, TextField } from "@material-ui/core";
import { Modal } from "react-bootstrap";
import { withCookies } from "react-cookie";

class Education extends Component {
  constructor(props) {
    super(props);

    this.state = {
      modelShow: true,
      credentials: {
        school: "",
        degree: "",
        field_of_study: "",
        start_year: "",
        end_year: "",
        user: this.props.cookies.get("auth-token").user.id,
      },
    };
  }

  inputChanged = (event) => {
    console.log(event.target.value);
    let cred = this.state.credentials;
    cred[event.target.name] = event.target.value;
    this.setState({ credentials: cred });
  };

  // For create new education for particular user
  createEducation = (e) => {
    fetch(`${process.env.REACT_APP_API_URL}/profile/education/`, {
      method: "POST",
      headers: {
        "Content-type": "application/json",
        Authorization: `Token ${this.props.cookies.get("auth-token").token}`,
      },
      body: JSON.stringify(this.state.credentials),
    })
      .then((resp) => resp.json())
      .then((resp) => {
        console.log(resp);
        this.props.cookies.set("education-id", resp.id);
        this.props.updateEducation();
      })
      .catch((error) => console.log(error));

    e.preventDefault();
    this.props.onEducationModal(false);
  };

  // For edit particular education for user.
  editEducation = (e) => {
    fetch(
      `${process.env.REACT_APP_API_URL}/profile/education/${this.props.editEducation_id}/`,
      {
        method: "PUT",
        headers: {
          "Content-type": "application/json",
          Authorization: `Token ${this.props.cookies.get("auth-token").token}`,
        },
        body: JSON.stringify(this.state.credentials),
      }
    )
      .then((resp) => resp.json())
      .then((resp) => {
        console.log(resp);
        this.setState({ modelShow: false });
        this.props.updateEducation();
      })
      .catch((error) => console.log(error));

    e.preventDefault();
    this.props.onEducationModal(false);
  };

  // for display education data in Modal
  getEducationData = () => {
    fetch(
      `${process.env.REACT_APP_API_URL}/profile/education/${this.props.editEducation_id}/`,
      {
        method: "GET",
        headers: {
          "Content-type": "application/json",
          Authorization: `Token ${this.props.cookies.get("auth-token").token}`,
        },
      }
    )
      .then((resp) => resp.json())
      .then((resp) => {
        // console.log(resp);
        if (resp) {
          this.setState({
            credentials: {
              school: resp.school,
              degree: resp.degree,
              field_of_study: resp.field_of_study,
              start_year: resp.start_year,
              end_year: resp.end_year,
              user: this.props.cookies.get("auth-token").user.id,
            },
          });
        }
      })
      .catch((error) => console.log(error));
  };

  deleteEducation = () => {
    fetch(
      `${process.env.REACT_APP_API_URL}/profile/education/${this.props.editEducation_id}/`,
      {
        method: "DELETE",
        headers: {
          "Content-type": "application/json",
          Authorization: `Token ${this.props.cookies.get("auth-token").token}`,
        },
      }
    )
      .then((resp) => this.props.updateEducation())

      .catch((error) => console.log(error));
  };

  componentDidMount() {
    if (!this.props.onCreateEducation) {
      this.getEducationData();
    }
  }

  render() {
    return (
      <Modal
        show={this.state.modelShow}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
        onHide={() => this.setState({ modelShow: false })}
      >
        <Modal.Header closeButton>Edit Education</Modal.Header>
        <Modal.Body className="profile__modal">
          {/* School */}
          <TextField
            rowsMax={4}
            rowsMin={4}
            variant="outlined"
            size="large"
            type="text"
            fullWidth
            id="school"
            value={this.state.credentials.school}
            label="School"
            name="school"
            onChange={this.inputChanged}
            required
            className="mb-3"
          />
          {/* Degree */}
          <TextField
            rowsMax={4}
            rowsMin={4}
            variant="outlined"
            size="large"
            type="text"
            fullWidth
            id="degree"
            value={this.state.credentials.degree}
            label="Degree"
            name="degree"
            onChange={this.inputChanged}
            required
            className="mb-3"
          />
          {/* Field of study */}
          <TextField
            rowsMax={4}
            rowsMin={4}
            variant="outlined"
            size="large"
            type="text"
            fullWidth
            id="field_of_study"
            value={this.state.credentials.field_of_study}
            label="Field of study"
            name="field_of_study"
            onChange={this.inputChanged}
            required
            className="mb-3"
          />
          {/* start year and end year */}
          <div style={{ display: "flex  " }}>
            <TextField
              className="mt-3 mb-5 mr-2"
              variant="outlined"
              size="small"
              type="text"
              fullWidth
              id="start_year"
              value={this.state.credentials.start_year}
              label="Start Year"
              name="start_year"
              onChange={this.inputChanged}
              required
            />
            <TextField
              className="mt-3 mb-5 "
              variant="outlined"
              size="small"
              type="text"
              fullWidth
              id="end_year"
              value={this.state.credentials.end_year}
              label="End Year"
              name="end_year"
              onChange={this.inputChanged}
              required
            />
          </div>
        </Modal.Body>
        <Modal.Footer>
          {this.props.onCreateEducation ? (
            <Button
              onClick={this.createEducation}
              style={save_button}
              type="submit"
            >
              Save
            </Button>
          ) : (
            <React.Fragment>
              <Button
                onClick={this.deleteEducation}
                style={save_button}
                type="submit"
                className="mr-auto"
              >
                Delete
              </Button>
              <Button
                onClick={this.editEducation}
                style={save_button}
                type="submit"
                className="ml-auto"
              >
                save
              </Button>
            </React.Fragment>
          )}
        </Modal.Footer>
      </Modal>
    );
  }
}

const save_button = {
  paddingLeft: "20px",
  paddingRight: "20px",
  marginTop: "10px",
  marginLeft: "10px",
  fontWeight: "bold",
  borderRadius: "50px",
  display: "flex",
  background: "#0c66c2",
  color: "white",
  border: "solid 1px #0c66c2",
};

export default withCookies(Education);
