import React, { Component } from "react";
import { Button } from "@material-ui/core";

class ActivityMain extends Component {
   constructor(props) {
       super(props)

       this.state = {
            all_activity_btn : true,
            post_btn:false
       }
   }

   handleActivity = () => {
    this.setState({all_activity_btn:true,post_btn:false})
   }

   handlePost = () => {
    this.setState({post_btn:true,all_activity_btn:false})
   }


  render() {
    return (
      <div className="feed">
        <div className="feed__inputContainer">
          <div>
            <span style={{ fontWeight: "bold", fontSize: "25px",paddingLeft:"10px" }}>
              Malav's Activity
            </span>
          </div>
          <div style={{ display: "flex" }} className="mt-2">
            <Button style={ this.state.all_activity_btn ? Active_btn : non_Active_btn} onClick={this.handleActivity}>
              All activity
            </Button>
            <Button style={non_Active_btn} onClick={this.handlePost} >
              Post
            </Button>
          </div>

          {/* <button
            style={{ width: "100%" }}
            // onClick={this.createPost}
            className="feed__input"
          >
            Start a Post
          </button> */}
        </div>
        <hr />

        {/* Posts */}
        {/* <FlipMove>
          {this.state.posts.length > 0 &&
