        </FlipMove>

        {this.state.modalPost ? (
          <Link component={() => <CreatePost />} />
        ) : null} */}
      </div>
    );
  }
}

const Active_btn = {
  paddingLeft: "20px",
  paddingRight: "20px",
  marginTop: "10px",
  marginLeft: "10px",
  fontWeight: "bold",
  borderRadius: "50px",
  display: "flex",
  background: "#2E7742",
  color: "white",
  border: "solid 1px #0c66c2",
};

const non_Active_btn = {
    paddingLeft: "20px",
    paddingRight: "20px",
    marginTop: "10px",
    marginLeft: "10px",
    fontWeight: "bold",
    borderRadius: "50px",
    display: "flex",
    background: "white",
    color: "grey",
    border: "solid 1px black",
  };
export default ActivityMain;
