import React, { Component } from "react";

export class AddProfilePic extends Component {
  constructor(props) {
    super(props);

    this.state = {
      modalShow: true,
      profile_pic: this.props.profile_pic,
      profile_picAsFile: this.props.profile_picAsFile,
    };
  }

  removeImage = () => {
    console.log("clicked");
    this.setState({
      profile_pic: "",
      profile_picAsFile: null,
    });
  };

  handleProfilePic = (e) => {
    this.setState(
      {
        profile_pic: URL.createObjectURL(e.target.files[0]),
        profile_picAsFile: e.target.files[0],
      },
      function () {
        var form_data = new FormData();

        if (this.state.profile_picAsFile) {
          form_data.set(
            "profile_pic",
            this.state.profile_picAsFile,
            this.state.profile_picAsFile.name
          );
        }
        console.log(this.props.cookies.get("auth-token").token);
        fetch(
          `${process.env.REACT_APP_API_URL}/uapi/users/${
            this.props.cookies.get("auth-token").user.id
          }/update_user/`,
          {
            method: "PUT",
            headers: {
              Authorization: `Token ${
                this.props.cookies.get("auth-token").token
              }`,
            },
            body: form_data,
          }
        )
          .then((res) => res.json())
          .then((res) => {
            window.location.reload();
            console.log("response", res);
          })
          .catch((err) => console.log(err));
      }
    );
  };

  render() {
    return (
      <div>
        <Modal
          style={{
            height: "700px",
          }}
          scrollable={true}
          show={this.state.modalShow}
          size="md"
          aria-labelledby="contained-modal-title-vcenter"
          centered
        >
          <Modal.Header
            closeButton
            autoFocus
            onClick={() => this.setState({ modalShow: false })}
          >
            <Modal.Title
              style={{
                fontSize: "1.2rem",
              }}
              id="contained-modal-title-vcenter"
            >
              Profile Photo
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            {this.props.profile_pic ? (
              <React.Fragment>
                <div className="img_wrp">
                  <img
                    className="post__image"
                    style={{ borderRadius: "10px" }}
                    src={this.props.profile_pic}
                  />
                  <AiFillCloseCircle
                    className="close__image"
                    onClick={this.removeImage}
                  />
                  {/* <Icon  /> */}
                </div>
              </React.Fragment>
            ) : null}
          </Modal.Body>
          <Modal.Footer>
            <input
              style={{ display: "none" }}
              type="file"
              id="file"
              onChange={(e) =>
                this.setState({
                  image: URL.createObjectURL(e.target.files[0]),
                  imageAsFile: e.target.files[0],
                })
              }
            />
            <label className="mr-auto" htmlFor="file">
              <InsertPhotoIcon style={{ color: "gray" }} />
            </label>
            <Button
              style={post_button}
              disabled={this.state.body.length < 1}
              type="submit"
              onClick={this.submitPost}
            >
              Post
            </Button>
          </Modal.Footer>
        </Modal>
      </div>
    );
  }
}

export default AddProfilePic;
