import React, { Component } from "react";
import "./ProfileRight.css";
import { trackPromise } from "react-promise-tracker";
import FlipMove from "react-flip-move";
import UserContext from "../userContext";
import { Avatar, Button } from "@material-ui/core";
import { withCookies } from "react-cookie";
import { Link } from "react-router-dom";
import { MdDone } from "react-icons/md";

class ProfileRight extends Component {
  static contextType = UserContext;

  constructor(props) {
    super(props);

    this.state = {
      resp_user: [],
      users: [],
      pending: false,
    };
  }

  submitFollow = (e) => {
      console.log(e.target.value)
    // fetch(
    //   `${process.env.REACT_APP_API_URL}/uapi/follow/${this.props.user.id}/`,
    //   {
    //     method: "POST",
    //     headers: {
    //       "Content-Type": "application/json",
    //       Authorization: `Token ${this.props.cookies.get("auth-token").token}`,
    //     },
    //   }
    // )
    //   .then((resp) => resp.json())
    //   .then((resp) => {
    //     // console.log(resp);
    //     this.setState({ pending: true });
    //   })
    //   .catch((error) => console.log(error));

    // fetch(
    //   `${process.env.REACT_APP_API_URL}/uapi/waitFollow/${this.props.user.id}/`,
    //   {
    //     method: "POST",
    //     headers: {
    //       "Content-Type": "application/json",
    //       Authorization: `Token ${this.props.cookies.get("auth-token").token}`,
    //     },
    //   }
    // )
    //   .then((resp) => resp.json())
    //   .then((resp) => console.log(resp))
    //   .catch((error) => console.log(error));
  };


  usersDisplay = (user) => (
    <div className="profile_right__article">
      <div className="profile_right__articleLeft">
        <Avatar
          style={{ width: "60px", height: "60px" }}
          src={user.user.profile_pic}
          alt="Profile"
        />
      </div>

      <div className="profile_right__articleRight">
        <Link to={{ pathname: "user_profile", state: user.user.id }}>
          <span
            style={{
              fontWeight: "bold",
              color: "black",
              fontSize: "18px",
            }}
          >
            {user.user.username.length <= 10
              ? user.user.username
              : user.user.username.slice(0, 10) + ".."}
          </span>
        </Link>
        {this.state.pending ? (
          <Button
            onClick={this.submitFollow}
            className="but mt-3"
            style={pending_button}
          >
            <MdDone style={{ fontSize: "20px", marginRight: "2px" }} /> Pending
          </Button>
        ) : (
          <Button
            onClick={this.submitFollow}
            className="but mt-3"
            style={connect_button}
          >
            Connect
          </Button>
        )}
      </div>
    </div>
  );

  componentDidMount() {
    const user = this.context;
    trackPromise(
      fetch(`${process.env.REACT_APP_API_URL}/uapi/userDetail/`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      })
        .then((resp) => resp.json())
        .then((resp) => {
          this.setState({ resp_user: resp }, function () {
            var copy = [];
            if (user.user.followers && user.user.following) {
              for (var i = 0; i < this.state.resp_user.length; i++) {
                if (
                  user.user.id != this.state.resp_user[i].id &&
                  !user.user.followers.some(
                    (e) => e.user_id === this.state.resp_user[i].id
                  ) &&
                  !user.user.following.some(
                    (e) => e.following_user_id === this.state.resp_user[i].id
                  )
                  // &&
                  // !user.waitFollowers.some(
                  //   (e) => e.user_id === this.state.resp_user[i].id
                  // )
                ) {
                  copy.push(this.state.resp_user[i]);
                }
              }
            }
            this.setState({ users: copy });
          });
        })
    ).catch((error) => console.log(error));
  }

  render() {
    return (
      <div className="profile_right">
        <div className="profile_right__header">
          <span className="pl-1">People you may know</span>
        </div>
        {this.state.users
          ? this.state.users.map((user) => (
              <React.Fragment key={user.id}>
                {this.usersDisplay({ user })}
              </React.Fragment>
            ))
          : null}
      </div>
    );
  }
}

const connect_button = {
  fontWeight: "bold",
  borderRadius: "50px",
  fontSize: "12px",
  display: "flex",
  background: "white",
  color: "#0c66c2",
  border: "solid 1px #0c66c2",
  maxWidth: "100%",
  cursor: "pointer",
};
const pending_button = {
  fontWeight: "bold",
  borderRadius: "50px",
  fontSize: "12px",
  display: "flex",
  background: "white",
  color: "gray",
  border: "solid 1px gray",
  maxWidth: "100%",
  cursor: "pointer",
};

export default withCookies(ProfileRight);
