import React, { Component } from "react";
import { Button, TextField } from "@material-ui/core";
import { Modal } from "react-bootstrap";
import { withCookies } from "react-cookie";

class License extends Component {
  constructor(props) {
    super(props);

    this.state = {
      modelShow: true,
      credentials: {
        name: "",
        issuing_org: "",
        issue_date: null,
        expiration_date: null,
        credential_id: "",
        user: this.props.cookies.get("auth-token").user.id,
      },
    };
  }

  inputChanged = (event) => {
    console.log(event.target.value);
    let cred = this.state.credentials;
    cred[event.target.name] = event.target.value;
    this.setState({ credentials: cred });
  };

  // create new liecense or cetificate for currently login user
  createLicense = (e) => {
    fetch(`${process.env.REACT_APP_API_URL}/profile/license/`, {
      method: "POST",
      headers: {
        "Content-type": "application/json",
        Authorization: `Token ${this.props.cookies.get("auth-token").token}`,
      },
      body: JSON.stringify(this.state.credentials),
    })
      .then((resp) => resp.json())
      .then((resp) => {
        console.log(resp);
        this.props.cookies.set("license-id", resp.id);
        this.props.updateLicense();
      })
      .catch((error) => console.log(error));

    e.preventDefault();
    this.props.onLicenseModal(false);
  };

  // edit license or certificate for currently login user
  editLicense = (e) => {
    fetch(
      `${process.env.REACT_APP_API_URL}/profile/license/${this.props.editLicense_id}/`,
      {
        method: "PUT",
        headers: {
          "Content-type": "application/json",
          Authorization: `Token ${this.props.cookies.get("auth-token").token}`,
        },
        body: JSON.stringify(this.state.credentials),
      }
    )
      .then((resp) => resp.json())
      .then((resp) => {
        console.log(resp);
        this.setState({ modelShow: false });
        this.props.updateLicense();
      })
      .catch((error) => console.log(error));

    e.preventDefault();
    this.props.onLicenseModal(false);
  };

  // get data for particular license
  getLicenseData = () => {
    if (this.props.cookies.get("deleted-license-id") !== this.props.editLicense_id) {
      fetch(
        `${process.env.REACT_APP_API_URL}/profile/license/${this.props.editLicense_id}/`,
        {
          method: "GET",
          headers: {
            "Content-type": "application/json",
            Authorization: `Token ${
              this.props.cookies.get("auth-token").token
            }`,
          },
        }
      )
        .then((resp) => resp.json())
        .then((resp) => {
          // console.log(resp);
          if (resp) {
            this.setState({
              credentials: {
                name: resp.name,
                issuing_org: resp.issuing_org,
                issue_date: resp.issue_date,
                expiration_date: resp.expiration_date,
                credential_id: resp.credential_id,
                user: this.props.cookies.get("auth-token").user.id,
              },
            });
          }
        })
        .catch((error) => console.log(error));
    }
  };

  // delete license
  deleteLicense = (e) => {
    this.props.cookies.set("deleted-license-id", this.props.editLicense_id);
    fetch(
      `${process.env.REACT_APP_API_URL}/profile/license/${this.props.editLicense_id}/`,
      {
        method: "DELETE",
        headers: {
          "Content-type": "application/json",
          Authorization: `Token ${this.props.cookies.get("auth-token").token}`,
        },
      }
    )
      .then((resp) => this.props.updateLicense())

      .catch((error) => console.log(error));
  };


  componentDidMount() {
    if (!this.props.onCreateLicense) {
      this.getLicenseData();
    }
  }

  render() {
    return (
      <Modal
        show={this.state.modelShow}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
        onHide={() => this.setState({ modelShow: false })}
      >
        <Modal.Header closeButton>Edit license or certification</Modal.Header>
        <Modal.Body className="profile__modal">
          {/* name */}
          <TextField
            rowsMax={4}
            rowsMin={4}
            variant="outlined"
            size="large"
            type="text"
            fullWidth
            id="name"
            value={this.state.credentials.name}
            label="Name"
            name="name"
            onChange={this.inputChanged}
            required
            className="mb-3"
          />
          {/* issuing_org */}
          <TextField
            rowsMax={4}
            rowsMin={4}
            variant="outlined"
            size="large"
            type="text"
            fullWidth
            id="issuing_org"
            value={this.state.credentials.issuing_org}
            label="Issuing_organization"
            name="issuing_org"
            onChange={this.inputChanged}
            required
            className="mb-3"
          />

          {/* issue_date and expiration date */}
          <div style={{ display: "flex  " }}>
            <TextField
              className="mt-3 mb-5 mr-2"
              variant="outlined"
              size="small"
              type="text"
              fullWidth
              id="start_year"
              value={this.state.credentials.start_year}
              label="Start Year"
              name="start_year"
              onChange={this.inputChanged}
              required
            />
            <TextField
              className="mt-3 mb-5 "
              variant="outlined"
              size="small"
              type="text"
              fullWidth
              id="end_year"
              value={this.state.credentials.end_year}
              label="End Year"
              name="end_year"
              onChange={this.inputChanged}
              required
            />
          </div>
          {/* credential_id */}
          <TextField
            rowsMax={4}
            rowsMin={4}
            variant="outlined"
            size="large"
            type="text"
            fullWidth
            id="credential_id"
            value={this.state.credentials.credential_id}
            label="Credential ID"
            name="credential_id"
            onChange={this.inputChanged}
            required
            className="mb-3"
          />
        </Modal.Body>
        <Modal.Footer>
          {this.props.onCreateLicense ? (
            <Button
              onClick={this.createLicense}
              style={save_button}
              type="submit"
            >
              Save
            </Button>
          ) : (
            <React.Fragment>
              <Button
                onClick={this.deleteLicense}
                style={save_button}
                type="submit"
                className="mr-auto"
              >
                Delete
              </Button>
              <Button
                onClick={this.editLicense}
                style={save_button}
                type="submit"
                className="ml-auto"
              >
                save
              </Button>
            </React.Fragment>
          )}
        </Modal.Footer>
      </Modal>
    );
  }
}

const save_button = {
  paddingLeft: "20px",
  paddingRight: "20px",
  marginTop: "10px",
  marginLeft: "10px",
  fontWeight: "bold",
  borderRadius: "50px",
  display: "flex",
  background: "#0c66c2",
  color: "white",
  border: "solid 1px #0c66c2",
};

export default withCookies(License);
