import "./Feed.css";
import InputOption from "./InputOption";
import Post from "./Post";
import CreateIcon from "@material-ui/icons/Create";
import ImageIcon from "@material-ui/icons/Image";
import SubscriptionsIcon from "@material-ui/icons/Subscriptions";
import EventNoteIcon from "@material-ui/icons/EventNote";
import CalendarViewDayIcon from "@material-ui/icons/CalendarViewDay";
import { Card, Col, Row, Container, Modal, Button } from "react-bootstrap";
import InsertPhotoIcon from "@material-ui/icons/InsertPhoto";

// import { useSelector } from 'react-redux';
// import { selectUser } from '../../features/userSlice';
// import { db } from '../../firebase';
// import firebase from "firebase";
import FlipMove from "react-flip-move";

import React, { Component } from "react";

class Feed extends Component {
  constructor(props) {
    super(props);

    this.state = {
      input: "",
      posts: [],
      modalPost: false,
      body: "",
      image: "",
    };
  }

  submitPost = () => {
    fetch("http://127.0.0.1:8000/papi/create_post/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Token 8dd51a875692f1fef8a3cd9f45b08798b9a303b4",
      },

      body: JSON.stringify({
        stars: stars + 1,
      }),
    })
      .then((resp) => resp.json())
      .catch((error) => console.log(error));
  };

  onFileChange(event) {
    //console.log(event.target.files[0].name)
    var blob = event.target.files[0].slice(
      0,
      event.target.files[0].size,
      "image/png"
    );

    const newFile = new File([blob], this.dat.imageName, { type: "image/png" });
    //console.log(newFile)
  }
  createPost = () => {
    this.setState(
      {
        modalPost: true,
      },
      console.log(this.state.modalPost)
    );
  };

  handleBody = (e) =>
    this.setState({ body: e.target.value }, function () {
      console.log(this.state.body);
    });

  componentDidMount() {
    fetch("http://127.0.0.1:8000/papi/posts/", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Token 8dd51a875692f1fef8a3cd9f45b08798b9a303b4",
      },
    })
      .then((resp) => resp.json())
      .then((res) =>
        this.setState(
          {
            posts: res,
          },
          console.log(this.state.posts)
        )
      )
      .catch((error) => console.log(error));
  }

  render() {
    return (
      <div className="feed">
        <div className="feed__inputContainer">
          {/* <div className="feed__input"> */}
          {/* <CreateIcon /> */}
          {/* <form>
              <input
                value={this.state.input}
                onChange={(e) => this.setState({ input: e.target.value })}
                type="text"
                placeholder="Start a post"
              /> */}
          {/* <button onClick={sendPost} type="submit">Send</button> */}
          {/* </form> */}
          <Button onClick={this.createPost} className="feed__input">
            Start a Post
          </Button>
          {/* </div> */}
          <div className="feed__inputOptions">
            <InputOption Icon={ImageIcon} title="Photo" color="#70B5F9" />
            <InputOption
              Icon={SubscriptionsIcon}
              title="Video"
              color="#7FC15E"
            />
            <InputOption Icon={EventNoteIcon} title="Event" color="#E7A33E" />
            <InputOption
              Icon={CalendarViewDayIcon}
              title="Write article"
              color="#F5987E"
            />
          </div>
        </div>
        <hr />

        {/* Posts */}
        <FlipMove>
          {this.state.posts.map((post) => (
            <Post
              key={post.id}
              body={post.body}
              // description={description}
              // message={message}
              user={post.user}
              image={post.image}
              likes={post.no_of_like}
              comment={post.no_of_comment}
            />
          ))}
        </FlipMove>

        {this.state.modalPost ? (
          <Modal
            show={this.state.modalPost}
            size="md"
            aria-labelledby="contained-modal-title-vcenter"
            centered
          >
            <Modal.Header
              closeButton
              onClick={() => {
                this.setState({ modalPost: false });
              }}
            >
              <Modal.Title id="contained-modal-title-vcenter">
                Create a Post
              </Modal.Title>
            </Modal.Header>
            <Modal.Body>
              {/* <p>
                Cras mattis consectetur purus sit amet fermentum. Cras justo
                odio, dapibus ac facilisis in, egestas eget quam. Morbi leo
                risus, porta ac consectetur ac, vestibulum at eros.
              </p> */}
              <input
                placeholder="What do you want to talk about?"
                // value={this.state.body}
                onChange={this.handleBody}
              />
              {this.state.image ? (
                <img className="post__image" src={this.state.image} />
              ) : null}
            </Modal.Body>
            <Modal.Footer>
              <Button
                disabled={this.state.body.length < 1}
                type="submit"
                onClick={this.submitPost}
              >
                Post
              </Button>
              <input
                type="file"
                // value={this.state.image}
                onChange={(e) =>
                  this.setState(
                    {
                      image: URL.createObjectURL(e.target.files[0]),
                    },
                    console.log(this.state.image)
                  )
                }
              />
            </Modal.Footer>
          </Modal>
        ) : null}
      </div>
    );
  }
}

export default Feed;
