import React, { Component } from "react";
import { Button, TextareaAutosize } from "@material-ui/core";
import { Modal } from "react-bootstrap";
import { withCookies } from "react-cookie";

class About extends Component {
  constructor(props) {
    super(props);

    this.state = {
      modelShow: true,
      about: "hello",
    };
  }

  inputChanged = (event) => {
    console.log(event.target.value);
    this.setState({ about: event.target.value });
  };

  render() {
    return (
      <Modal
        show={this.state.modelShow}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
        onHide={() => this.setState({ modelShow: false })}
      >
        <Modal.Header closeButton>Edit About</Modal.Header>
        <Modal.Body className="profile__modal">

          {/* About */}
          <TextareaAutosize
            variant="outlined"
            size="small"
            type="text"
            fullWidth
            id="about"
            value={this.state.about}
            label="About"
            name="about"
            onChange={this.inputChanged}
            required
            className="mb-3"
          />

        </Modal.Body>
        <Modal.Footer>
          {this.props.cookies.get("profile") === "true" ? (
            <Button
            //   onClick={this.editProfile}
              style={save_button}
              type="submit"
            >
              save
            </Button>
          ) : (
            <Button
            //   onClick={this.createProfile}
              style={save_button}
              type="submit"
            >
              Save
            </Button>
          )}
        </Modal.Footer>
      </Modal>
    );
  }
}

const save_button = {
    paddingLeft: "20px",
    paddingRight: "20px",
    marginTop: "10px",
    marginLeft: "10px",
    fontWeight: "bold",
    borderRadius: "50px",
    display: "flex",
    background: "#0c66c2",
    color: "white",
    border: "solid 1px #0c66c2",
  };

export default withCookies(About);
